--
-- Cấu trúc bảng cho bảng `cars`
--

CREATE TABLE `cars` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `name` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `price` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL,
  `content` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `user_id` int(11) NOT NULL,
  `category_car_id` int(11) NOT NULL,
  `feature_image_path` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `feature_image_name` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `status` int(11) NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL,
  `description` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci ROW_FORMAT=DYNAMIC;

--
-- Đang đổ dữ liệu cho bảng `cars`
--

INSERT INTO `cars` (`id`, `name`, `price`, `content`, `user_id`, `category_car_id`, `feature_image_path`, `feature_image_name`, `status`, `created_at`, `updated_at`, `deleted_at`, `description`) VALUES
(1, 'bmw i8', '2000000', '<h2><strong>Gi&aacute; BMW i8 2021 mới nhất th&aacute;ng 7</strong></h2>\r\n\r\n<p>Tại&nbsp;Mỹ,<strong>&nbsp;gi&aacute; xe&nbsp;i8 BMW</strong>&nbsp;cho&nbsp;hai phi&ecirc;n bản l&agrave; Coupe v&agrave; Roadster được&nbsp;thiết kế mui cứng v&agrave; mui trần lần lượt&nbsp;ở mức 147.500 USD v&agrave; 163.300 USD, tương đương 3,4 v&agrave; 3,8 tỷ đồng.</p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"3\"><strong>Gi&aacute; xe BMW i8 tại thị trường nước ngo&agrave;i</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>Phi&ecirc;n bản xe</strong></td>\r\n			<td><strong>Gi&aacute; xe (USD)</strong></td>\r\n			<td><strong>Gi&aacute; quy đổi (VNĐ)</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>BMW i8 Coupe</strong></td>\r\n			<td>147.500</td>\r\n			<td>3.400.000.000</td>\r\n		</tr>\r\n		<tr>\r\n			<td><strong>BMW i8 Roadster</strong></td>\r\n			<td>163.300</td>\r\n			<td>3.800.000.000</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><em>Gi&aacute; xe BMW i8 mui trần v&agrave; mui cứng ở nước ngo&agrave;i.</em></p>\r\n\r\n<p>Th&ocirc;ng qua c&aacute;c đơn vị kinh doanh xe nhập khẩu tư nh&acirc;n n&ecirc;n gi&aacute; xe&nbsp;thể thao BMW i8 về Việt Nam kh&aacute; cao. Theo khảo s&aacute;t,&nbsp;<a href=\"https://tinxe.vn/gia-xe-bmw-i8\"><strong>gi&aacute; xe BMW i8</strong></a>&nbsp;tại nước ta ở&nbsp;mức 7 tỷ đồng, đắt gấp đ&ocirc;i so với Mỹ. Trong đ&oacute;, gi&aacute; xe i8 đời cũ sẽ rẻ hơn nhiều.</p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"1\" cellspacing=\"1\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"3\"><strong>Gi&aacute; BMW i8 Việt Nam</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" rowspan=\"1\"><strong>T&ecirc;n xe</strong></td>\r\n			<td><strong>Gi&aacute; xe BMW i8 mới</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\" rowspan=\"1\"><strong>BMW i8</strong></td>\r\n			<td>khoảng 8 tỷ đồng</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"3\"><strong>Gi&aacute; BMW i8 đ&atilde; qua sử dụng</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"1\"><strong>T&ecirc;n xe</strong></td>\r\n			<td><strong>Đời xe</strong></td>\r\n			<td><strong>Gi&aacute; xe BMW i8 cũ</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"1\" rowspan=\"3\"><strong>BMW i8 cũ</strong></td>\r\n			<td>2014</td>\r\n			<td>3,8 - 4,3 tỷ đồng</td>\r\n		</tr>\r\n		<tr>\r\n			<td>2015</td>\r\n			<td>3,8 - 4,5 tỷ đồng</td>\r\n		</tr>\r\n		<tr>\r\n			<td>2016</td>\r\n			<td>4,2 - 4,5 tỷ đồng</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>', 13, 1, '/storage/cars/13/z5JawBEcgv4DkFolhbue.jpg', 'gia-xe-bmw-i8-4-1779.jpg', 0, '2021-07-16 18:07:23', '2021-07-19 07:22:29', NULL, NULL),
(2, 'Xe Hyundai 16 chỗ Solati 2020 vận hành vượt trội', '980000', '<h2><strong>Ngoại thất Hyundai 16 chỗ đậm chất ch&acirc;u &acirc;u</strong></h2>\r\n\r\n<p>Phần đầu xe mang h&igrave;nh d&aacute;ng quen thuộc của h&agrave;ng ngũ c&aacute;c th&agrave;nh vi&ecirc;n xe chở kh&aacute;ch của h&atilde;ng với tấm tản nhiệt h&igrave;nh gi&aacute;c với 2 thanh kim loại đặt ngang được sơn đen nổi bật nền m&agrave;u c&ugrave;ng logo Hyundai đặt ch&iacute;nh giữa, hai b&ecirc;n l&agrave; cụm đ&egrave;n pha g&oacute;c cạnh c&oacute; điểm nhấn nằm ở c&ocirc;ng nghệ Projector (b&oacute;ng chiếu) v&agrave; dải LED chiếu s&aacute;ng ban ng&agrave;y c&aacute; t&iacute;nh.&nbsp;được sơn đen nổi bật tr&ecirc;n nền m&agrave;u ngoại thất, với logo Hyundai tại trung t&acirc;m.</p>\r\n\r\n<p><img alt=\"Xe hyundai 16 chỗ Solati mới\" src=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/xe-khach-hyundai-solati-16-cho-moi-e1559172538447.jpg\" style=\"height:450px; width:800px\" /></p>\r\n\r\n<p><a href=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/den-suong-mu-solati-16-cho.jpg\"><img alt=\"\" src=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/den-suong-mu-solati-16-cho.jpg\" style=\"height:600px; width:800px\" /></a></p>\r\n\r\n<p><a href=\"https://hyundaianlac.vn/wp-content/uploads/2018/06/IMG_0257.jpg\"><img alt=\"\" src=\"https://hyundaianlac.vn/wp-content/uploads/2018/06/IMG_0257.jpg\" style=\"height:600px; width:800px\" /></a></p>\r\n\r\n<p><a href=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/Den-pha-xe-khach-solati.jpg\"><img alt=\"\" src=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/Den-pha-xe-khach-solati.jpg\" style=\"height:450px; width:600px\" /></a></p>\r\n\r\n<p>Xe c&oacute; phần cản trước sơn tối m&agrave;u, tương tự lưới tản nhiệt, hốc gi&oacute; thu lại rất hẹp, c&ograve;n đ&egrave;n sương m&ugrave; h&igrave;nh thoi g&oacute;c cạnh như tr&ecirc;n một mẫu SUV. Trong khi đ&oacute;, h&ocirc;ng xe lại kh&ocirc;ng thực sự nổi bật với bộ lazang đ&uacute;c hợp kim, b&ugrave; lại, chi tiết n&agrave;y mang đến cảm gi&aacute;c chắc chắn hơn.</p>\r\n\r\n<p><img alt=\"Kích thước xe Hyundai Solati 16 chỗ\" src=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/Hyundai-Solati-16-cho-3.jpg\" style=\"height:445px; width:800px\" /></p>\r\n\r\n<p>​Sở hữu k&iacute;ch thước tổng thể xe Hyundai 16 chỗ (d&agrave;i x rộng x cao) l&ecirc;n đến 6195 x 2038 x 2665mm,&nbsp;<strong>xe Hyundai Solati 2020</strong>&nbsp;cho thấy sự vượt trội về k&iacute;ch thước so với c&aacute;c d&ograve;ng xe trong ph&acirc;n kh&uacute;c 16 chỗ, bề thế, vu&ocirc;ng vắn v&agrave; c&oacute; phần sang trọng hơn, kh&ocirc;ng c&ograve;n trung t&iacute;nh v&agrave; nh&agrave;m ch&aacute;n.</p>\r\n\r\n<p><img alt=\"Phía sau đuôi solati Hyundai\" src=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/IMG_0252.jpg\" style=\"height:600px; width:800px\" /></p>\r\n\r\n<p><a href=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/cum-den-hau-solati-16-cho.jpg\"><img alt=\"\" src=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/cum-den-hau-solati-16-cho.jpg\" style=\"height:600px; width:800px\" /></a></p>\r\n\r\n<p><a href=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/IMG_E0274.jpg\"><img alt=\"\" src=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/IMG_E0274.jpg\" style=\"height:600px; width:800px\" /></a></p>\r\n\r\n<p>Ph&acirc;̀n mặt sau, Solati tạo sự kh&aacute;c biệt về trang bị so với c&aacute;c mẫu 16 chỗ tr&ecirc;n thị trường khi cụm đ&egrave;n hậu được tạo h&igrave;nh dạng cột, ph&ugrave; hợp với mục đ&iacute;ch b&aacute;o t&iacute;n hiệu trực quan đến người l&aacute;i ph&iacute;a sau và d&ecirc;̃ dàng sữa chữa cũng như thay th&ecirc;́. Cản sau được sơn đen, gi&uacute;p xe cứng c&aacute;p hơn, t&ocirc;́i ưu va chạm và chi phí sửa chữa. Xe c&oacute; t&iacute;ch hợp đ&egrave;n LED tr&ecirc;n cao đảm bảo an toàn cho các phương ti&ecirc;̣n khác phía sau.</p>\r\n\r\n<p>Tại c&aacute;c thị trường kh&aacute;c mẫu xe Solati H350, d&ograve;ng&nbsp;<a href=\"https://hyundai-truongchinh.info/xe/xe-hyundai-16-cho-solati/\" rel=\"noopener noreferrer\" target=\"_blank\"><strong>xe kh&aacute;ch 16 chỗ Hyundai solati</strong></a>&nbsp;c&ograve;n được cho ra mắt với nhiều kiểu d&aacute;ng v&agrave; chức năng kh&aacute;c nhau: xe chở h&agrave;ng, xe tải cỡ nhỏ, xe tải van 6 chỗ, 9 chỗ hay 12 chỗ tiện dụng. Kh&aacute;ch h&agrave;ng sẽ c&oacute; thể lựa chọn dựa theo chiều cao mui xe hoặc chiều d&agrave;i th&acirc;n xe ph&ugrave; hợp với nhu cầu.</p>\r\n\r\n<p><img alt=\"Hyundai Solati được thiết kế thành xe chở hàng\" src=\"https://hyundaianlac.vn/wp-content/uploads/2018/06/h350-xe-tai-hyundai-solati.jpg\" style=\"height:290px; width:799px\" /></p>\r\n\r\n<p>Hyundai Solati được thiết kế th&agrave;nh xe tải th&ugrave;ng lửng</p>\r\n\r\n<p>Để đ&aacute;p ứng tốt nhu cầu chở h&agrave;ng,&nbsp;<a href=\"https://hyundaianlac.vn/san-pham/xe-hyundai-16-cho--xe-van-co-lon-h350\"><strong>Hyundai H350</strong></a>&nbsp;sở hữu khoang chứa h&agrave;ng d&agrave;i 3,78m, rộng 1,795m v&agrave; cao 1,955m. Trong khoang chứa c&ograve;n bao gồm 14 điểm m&oacute;c v&agrave; một tấm v&aacute;ch ngăn bằng th&eacute;p để ngăn giữa khoang h&agrave;ng v&agrave; cabin xe.</p>\r\n\r\n<p><img alt=\"Hyundai Solati được thiết kế thành xe tải van \" src=\"https://hyundaianlac.vn/wp-content/uploads/2018/06/h350-xe-hyundai-solati-cho-hang.jpg\" style=\"height:455px; width:848px\" /></p>\r\n\r\n<p>Hyundai Solati được thiết kế th&agrave;nh xe tải van 3 chỗ</p>\r\n\r\n<p>Với khoang chứa h&agrave;ng c&oacute; k&iacute;ch thước tương đối d&agrave;i nh&agrave; sản xuất cho biết&nbsp;<strong>Hyundai H350</strong>&nbsp;c&oacute; khả năng chứa h&agrave;ng h&oacute;a trọng lượng tối đa 1,4 tấn, ngo&agrave;i ra c&oacute; thể k&eacute;o theo rờ-m&oacute;oc trọng lượng tối đa 2,5 tấn.</p>\r\n\r\n<h2><strong>Nội thất xe Hyundai 16 chỗ rộng r&atilde;i, tiện nghi</strong></h2>\r\n\r\n<p>Thiết kế nội thất của&nbsp;<a href=\"https://hyundai-ngocan.com/xe/xe-hyundai-16-cho-solati/\" rel=\"noopener noreferrer\" target=\"_blank\"><strong>Hyundai solati</strong></a>&nbsp;H350 vẫn mang hơi hướng cổ điển của những chiếc xe van th&ocirc;ng thường tuy nhi&ecirc;n được trang bị th&ecirc;m v&ocirc;-lăng đa năng, cửa sổ điện, hệ thống &acirc;m thanh CD/MP3 v&agrave; kết nối USB/Bluetooth/iPod đa dạng. Ngo&agrave;i ra chiếc xe c&ograve;n được lắp đặt điều h&ograve;a kh&ocirc;ng kh&iacute; c&ugrave;ng m&agrave;n h&igrave;nh LCD TFT 4.2-inch.</p>\r\n\r\n<p><img alt=\" Nội thất xe 16 chỗ Hyundai H350\" src=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/noi_that_Hyundai_Solati-16-cho.jpg\" style=\"height:506px; width:800px\" /></p>\r\n\r\n<p>Nội thất được thiết kế theo phong c&aacute;ch cổ điển v&agrave; t&iacute;ch hợp nhiều thiết bị hiện đại</p>\r\n\r\n<p>Hơn thế nữa, kh&aacute;ch h&agrave;ng c&oacute; thể t&ugrave;y &yacute; lựa chọn việc lắp đặt hệ thống th&ocirc;ng tin giải tr&iacute; chạy tr&ecirc;n nền tảng hệ điều h&agrave;nh Android trong đ&oacute; t&iacute;ch hợp chương tr&igrave;nh định vị GPS v&agrave; bản đồ 3D. Tuy nội thất của xe được thiết kế theo phong c&aacute;ch cổ điển nhưng lại được trang bị v&ocirc; lăng đa năng, cửa sổ điện hiện đại.</p>\r\n\r\n<p><a href=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/vo-lang-solati-16-cho.jpg\"><img alt=\"\" src=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/vo-lang-solati-16-cho.jpg\" style=\"height:600px; width:800px\" /></a></p>\r\n\r\n<p><a href=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/noi-that-xe-khach-16-cho-hyundai.jpg\"><img alt=\"\" src=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/noi-that-xe-khach-16-cho-hyundai.jpg\" style=\"height:600px; width:800px\" /></a></p>\r\n\r\n<p><a href=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/IMG_8002.jpg\"><img alt=\"\" src=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/IMG_8002.jpg\" style=\"height:600px; width:800px\" /></a></p>\r\n\r\n<p>Tương tự như c&aacute;c mẫu 16 chỗ kh&aacute;c,&nbsp;<strong>xe 16 cho moi nhat hyundai</strong>&nbsp;với hệ thống điều ho&agrave; cũng được thiết kế c&aacute;c cửa gi&oacute; ri&ecirc;ng cho từng h&agrave;ng ghế, ngay cả kệ h&agrave;nh l&yacute; tr&ecirc;n trần xe kh&aacute;ch 16 hyundai cũng được bố tr&iacute; đầy đủ c&aacute;c hộc gi&oacute; gi&uacute;p kh&ocirc;ng gian m&aacute;t mẻ nhanh ch&oacute;ng, mang đến cảm gi&aacute;c dễ chịu cho h&agrave;nh kh&aacute;ch c&ugrave;ng h&agrave;nh l&yacute;. Đặc bi&ecirc;̣t, Hyundai Solati còn có th&ecirc;m h&ecirc;̣ th&ocirc;́ng tu&acirc;̀n hoàn gió tr&ecirc;n tr&acirc;̀n xe. H&ecirc;̣ th&ocirc;́ng này giúp cho người ng&ocirc;̀i tr&ecirc;n xe thoải mái hơn r&acirc;́t nhi&ecirc;̀u khi lu&ocirc;̀ng gió được cung c&acirc;́p thường xuy&ecirc;n, ko g&acirc;y ng&ocirc;̣t ngạt, bí bách.</p>\r\n\r\n<p><img src=\"https://www.oto-saigon.com/uploads/2/5/0/6/25061710/485910001_orig.jpg\" style=\"height:508px; width:801px\" /></p>\r\n\r\n<p>​T&acirc;́t cả 16 gh&ecirc;́ ng&ocirc;̀i của Solati đ&ecirc;̀u là gh&ecirc;́ da r&acirc;́t m&ecirc;̀m mại được ph&ocirc;́i hai màu đen-xanh nhẹ nhàng, bắt mắt, có th&ecirc;̉ ngả ra sau giúp hành khách d&ecirc;̃ chịu khi di chuy&ecirc;̉n xa. Khoảng cách từ đ&acirc;̀u g&ocirc;́i đ&ecirc;́n hàng gh&ecirc;́ trước khá xa và thoải mái hơn r&acirc;́t nhi&ecirc;̀u. Đặc bi&ecirc;̣t, xe 16 chỗ hyundai được thi&ecirc;́t k&ecirc;́ 2 hàng gh&ecirc;́ với l&ocirc;́i đi ở giữa r&acirc;́t linh hoạt, kh&ocirc;ng cản trở đ&ecirc;́n người ng&ocirc;̀i phía ngoài khi có hành khách phía trong bước ra, đ&acirc;y là 1 đi&ecirc;̉m c&ocirc;̣ng so với đ&ocirc;́i thủ&nbsp;<strong>Ford Transit</strong>.</p>\r\n\r\n<h2><strong>An to&agrave;n tr&ecirc;n xe Hyundai Solati 16 chỗ</strong></h2>\r\n\r\n<p><img alt=\"\" src=\"https://hyundai-truongchinh.info/wp-content/uploads/2018/06/H350-page-025.jpg\" style=\"height:538px; width:800px\" /></p>\r\n\r\n<p>Trang bị an toàn&nbsp;<a href=\"https://www.oto-saigon.com/solati-16-cho-h350/gia-xe-khach-hyundai-16-cho-solati-moi\"><strong>Hyundai Solati 16 chỗ</strong></a>&nbsp;vượt mặt Ford Transit và Solati phi&ecirc;n bản trước do Thaco lắp ráp khi có tới 2 túi khí tr&ecirc;n gh&ecirc;́ lái và gh&ecirc;́ hành khách. D&acirc;y đai an toàn hoạt đ&ocirc;̣ng khi túi khí bung giúp an toàn hơn khi lái xe.</p>\r\n\r\n<p><img alt=\"\" src=\"https://hyundai-truongchinh.info/wp-content/uploads/2018/06/Thang-dia-va-ABS.jpg\" style=\"height:347px; width:800px\" /></p>\r\n\r\n<p>Cùng đó là h&ecirc;̣ th&ocirc;́ng ch&ocirc;́ng bó cứng phanh ABS ph&acirc;n b&ocirc;̉ ở cả 4 bánh h&ocirc;̃ trợ vững chắc tay lái khi gặp đoạn đường trơn trượt g&acirc;y m&acirc;́t ki&ecirc;̉m soát.</p>\r\n\r\n<p><img alt=\"\" src=\"https://hyundai-truongchinh.info/wp-content/uploads/2018/06/an-toan-tren-hyundai-h350-1.jpg\" style=\"height:536px; width:800px\" /></p>\r\n\r\n<p>Khung xe&nbsp;<a href=\"https://hyundaianlac.vn/san-pham/xe-hyundai-16-cho-xe-van-co-lon-h350\" rel=\"noopener noreferrer\" target=\"_blank\"><strong>Hyundai Solati</strong></a>&nbsp;với 80% l&agrave; th&eacute;p cao cấp với to&agrave;n bộ phần khung body l&agrave;m từ th&eacute;p hợp kim cao cấp, được thiết kế theo cấu tr&uacute;c v&ograve;ng xoay k&iacute;n tăng độ vững chắc, ổn định.</p>\r\n\r\n<p><img alt=\"\" src=\"https://hyundai-truongchinh.info/wp-content/uploads/2018/06/H350-page-024.jpg\" style=\"height:539px; width:800px\" /></p>\r\n\r\n<p>Khung chassis được thiết kế với kết cấu gh&eacute;p chồng đem lại hiệu quả hấp thụ lực va chạm tốt nhất, tăng độ an to&agrave;n.</p>\r\n\r\n<h2>Khả năng vận h&agrave;nh vượt trội tr&ecirc;n xe 16 chỗ Hyundai Solati</h2>\r\n\r\n<p><img alt=\"\" src=\"https://hyundai-truongchinh.info/wp-content/uploads/2018/06/H350-page-022.jpg\" style=\"height:539px; width:800px\" /></p>\r\n\r\n<p>M&ocirc;̣t trong những đi&ecirc;̉m ưu vi&ecirc;̣t kh&ocirc;ng th&ecirc;̉ kh&ocirc;ng k&ecirc;̉ tới của Hyundai Solati chính là sức mạnh&nbsp;<strong>V&Acirc;̣N HÀNH</strong>&nbsp;và&nbsp; của chi&ecirc;́c xe. Sở hữu kh&ocirc;́i đ&ocirc;̣ng cơ 2.5l CRDI, dung tích xi lanh 2,947cc cho c&ocirc;ng su&acirc;́t cực đại l&ecirc;n đ&ecirc;́n 170 mã lực tại 3600 vòng / phút, m&ocirc; men xoắn cực đại 422Nm. Rõ ràng đ&acirc;y là những th&ocirc;ng s&ocirc;́ vượt tr&ocirc;̣i so với mức khoảng 138 mã lực và khoảng hơn 370Nm của đ&ocirc;́i thủ Ford transit, đi&ecirc;̀u này giúp Solati tự tin chinh phục mọi nẻo đường.</p>\r\n\r\n<p><img alt=\"\" src=\"https://hyundai-truongchinh.info/wp-content/uploads/2018/06/Dong-co-euro-4.jpg\" style=\"height:600px; width:784px\" /></p>\r\n\r\n<p>Hyundai trang bị cho Solati h&ocirc;̣p s&ocirc;́ sàn 6 c&acirc;́p mang lại hi&ecirc;̣u quả truy&ecirc;̀n đ&ocirc;̣ng t&ocirc;́t, mượt mà và &ecirc;m ái b&ecirc;n cạnh khả năng ti&ecirc;́t ki&ecirc;̣m nhi&ecirc;n li&ecirc;̣u t&ocirc;́i đa. Cùng với đó là h&ecirc;̣ th&ocirc;́ng giảm ch&acirc;́n phía sau bằng nhíp lá lớn nh&acirc;́t ph&acirc;n khúc đảm bảo cho khoang xe thấp so với mặt đất, dễ d&agrave;ng chất h&agrave;ng v&agrave; kh&ocirc;ng g&acirc;y l&uacute;n xe khi chở h&agrave;ng h&oacute;a nặng</p>\r\n\r\n<h3><img alt=\"\" src=\"https://hyundai-truongchinh.info/wp-content/uploads/2018/06/hop-so-san6-cap-hyundai-solati-16-cho.jpg\" style=\"height:533px; width:800px\" /></h3>\r\n\r\n<h3>Th&ocirc;ng số kỹ thuật Hyundai Solati H350</h3>\r\n\r\n<p><img alt=\"\" src=\"https://hyundai-truongchinh.info/wp-content/uploads/2018/06/Thong-so-ky-thuat-hyundai-16-cho-5.jpg\" style=\"height:545px; width:800px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://hyundai-truongchinh.info/wp-content/uploads/2018/06/Thong-so-ky-thuat-hyundai-16-cho-4-1.jpg\" style=\"height:550px; width:821px\" /><img alt=\"\" src=\"https://hyundai-truongchinh.info/wp-content/uploads/2018/06/Thong-so-ky-thuat-hyundai-16-cho-3.jpg\" style=\"height:548px; width:815px\" /></p>\r\n\r\n<h3>Gi&aacute; xe solati 16 chỗ 2020 l&agrave; bao nhi&ecirc;u? mua xe Solati 2020 ở đ&acirc;u gi&aacute; rẻ nhất, nhiều khuyến m&atilde;i nhất</h3>\r\n\r\n<p><strong>Gi&aacute; xe solati 2020</strong>&nbsp;hiện đang được Hyundai Th&agrave;nh C&ocirc;ng &ndash; Nh&agrave; nhập khẩu, sản xuất v&agrave; lắp r&aacute;p thương hiệu xe Hyundai duy nhất tại Việt Nam c&ocirc;ng bố với mức gi&aacute; b&aacute;n: 1.015.000.000đ.</p>\r\n\r\n<p>Tại thời điểm th&aacute;ng 06/2019 hiện Hyundai Ngọc An đại l&yacute; Uỷ Quyền của Hyundai Th&agrave;nh C&ocirc;ng tại Việt Nam cũng đang c&oacute; trương ch&igrave;nh khuyến m&atilde;i l&ecirc;n đến&nbsp;<strong>hơn 35 triệu đồng</strong>&nbsp;d&agrave;nh cho mẫu&nbsp;<em><strong>xe Solati sản xuất 2020</strong></em>&nbsp;v&agrave; đang giảm gi&aacute; l&ecirc;n đến hơn&nbsp;<strong>80 triệu đồng</strong>&nbsp;cho mẫu&nbsp;<em><strong>xe 16 chỗ Solati 2020</strong></em>.</p>\r\n\r\n<p><strong><u>Lưu &yacute;</u>:</strong><br />\r\nGi&aacute; tr&ecirc;n l&agrave; gi&aacute; tham khảo đ&atilde; c&oacute; VAT, chưa bao gồm c&aacute;c chi ph&iacute; lăn b&aacute;nh xe hay chi ph&iacute; ra biển số xe Solati 2019. Để biết th&ocirc;ng tin ch&iacute;nh x&aacute;c về c&aacute;c chương tr&igrave;nh khuyến m&atilde;i cụ thể qu&yacute; kh&aacute;ch h&agrave;ng li&ecirc;n hệ trực tiếp hoặc gọi v&agrave;o số&nbsp;<strong>Hotline: 0932.039.168</strong><br />\r\n(Xe c&oacute; sẵn giao ngay gi&aacute; cạnh tranh, gi&aacute; ưu đ&atilde;i đặc biệt cho kh&aacute;ch h&agrave;ng mua xe l&ocirc;, mua xe dự &aacute;n.)</p>\r\n\r\n<h3>Thủ tục mua xe Solati trả g&oacute;p ng&acirc;n h&agrave;ng</h3>\r\n\r\n<p><img alt=\"\" src=\"https://hyundaianlac.vn/wp-content/uploads/2019/05/thu-tuc-mua-xe-tra-gop.png\" style=\"height:600px; width:723px\" /></p>', 13, 2, '/storage/cars/13/JOxu0pA1axGDRCmQFTQZ.jpg', 'Hyundai_Solati_16_chỗ.jpg', 0, '2021-07-16 18:09:06', '2021-07-19 07:26:10', NULL, NULL),
(3, 'Toyota Fortuner', '2000000', '<h2>Xe Toyota Fortuner l&agrave; SUV 7 chỗ kh&ocirc;ng đối thủ ở Việt Nam d&ugrave; trước đ&acirc;y mẫu xe n&agrave;y kh&aacute; tai tiếng. Tuy nhi&ecirc;n, thế hệ mới hiện tại,&nbsp;Toyota Fortuner đ&atilde; thay đổi ho&agrave;n to&agrave;n với diện mạo mới bắt mắt, thể thao hơn, vận h&agrave;nh an to&agrave;n v&agrave; tiện nghi cũng được bổ sung th&ecirc;m.</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><strong>Th&ocirc;ng tin xe&nbsp;Toyota Fortuner&nbsp;2021</strong></h2>\r\n\r\n<p><strong>Ngoại thất xe Toyota Fortuner&nbsp;2021</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Với sự thay đổi ho&agrave;n to&agrave;n, Toyota Fortuner mới&nbsp;trở n&ecirc;n cứng c&aacute;p v&agrave; chắc chắn với c&aacute;c g&oacute;c cạnh được thiết kế to v&agrave; r&otilde; r&agrave;ng. Cụ thể, phần đầu xe trang bị lưới tản nhiệt h&igrave;nh s&oacute;ng lượn v&agrave; hệ thống chiếu s&aacute;ng full LED cho đ&egrave;n pha, đ&egrave;n chiếu s&aacute;ng ban ng&agrave;y, đ&egrave;n sương m&ugrave; trước v&agrave; đ&egrave;n hậu. Ngo&agrave;i ra, tất cả phi&ecirc;n bản đều trang bị cụm đ&egrave;n c&oacute; t&iacute;nh năng điều khiển tự động, tự động c&acirc;n bằng g&oacute;c chiếu, chế độ đ&egrave;n chờ dẫn đường.</p>\r\n\r\n<p>Ri&ecirc;ng đ&egrave;n sương m&ugrave; ph&iacute;a sau c&oacute; sự kh&aacute;c biệt khi trang bị n&agrave;y chỉ xuất hiện tr&ecirc;n 2 bản Legender. Ngo&agrave;i ra, Toyota Fortuner n&acirc;ng cấp c&ograve;n sở hữu loạt trang bị như gương chiếu hậu gập, chỉnh điện, t&iacute;ch hợp đ&egrave;n b&aacute;o rẽ, gạt mưa trước sau, sấy k&iacute;nh sau, thanh cản trước sau, chắn b&ugrave;n trước sau,....</p>\r\n\r\n<p><strong>Nội thất xe Toyota Fortuner&nbsp;2021</strong></p>\r\n\r\n<p>B&ecirc;n trong Toyota Fortuner bản tự động sở hữu v&ocirc; lăng 3 chấu&nbsp;bọc da ốp gỗ mạ bạc, điều chỉnh tay 4 hướng, t&iacute;ch hợp n&uacute;t điều khiển v&agrave; lẫy chuyển số. Ghế ngồi bọc da, ghế l&aacute;i v&agrave; ghế phụ đều c&oacute; khả năng chỉnh điện 8 hướng. Hệ thống điều h&ograve;a trong xe l&agrave; loại tự động 2 v&ugrave;ng.&nbsp;</p>\r\n\r\n<p>Trong khi đ&oacute;, bản số s&agrave;n chỉ l&agrave; v&ocirc; lăng urethane, kh&ocirc;ng c&oacute; lẫy chuyển số, ghế ngồi bọc nỉ, ghế l&aacute;i v&agrave; ghế phụ chỉnh tay lần lượt l&agrave; 6 v&agrave; 4 hướng. Hệ thống điều h&ograve;a chỉ l&agrave; loại chỉnh tay 2 v&ugrave;ng.</p>\r\n\r\n<p>D&ugrave; vậy, tất cả phi&ecirc;n bản đều trang bị ghế ngồi thể thao, h&agrave;ng ghế thứ 2&nbsp;c&oacute; thể gập 60:40 một chạm, chỉnh cơ 4 hướng, trong khi h&agrave;ng ghế thứ 3 ngả lưng ghế, gập 50:50 sang 2 b&ecirc;n, c&oacute; tựa tay h&agrave;ng ghế sau. Đồng thời, c&aacute;c vị tr&iacute; ngồi ph&iacute;a sau đều được l&agrave;m m&aacute;t nhờ cửa gi&oacute; sau v&agrave; trang bị th&ecirc;m hộp l&agrave;m m&aacute;t....</p>\r\n\r\n<p><br />\r\nVề trang bị an to&agrave;n, xe được trang bị hệ thống chống b&oacute; cứng phanh (ABS),&nbsp;hệ thống ph&acirc;n bổ lực phanh điện tử (EBD),&nbsp;hệ thống hỗ trợ khởi động ngang dốc (HAC),&nbsp;hệ thống kiểm so&aacute;t lực k&eacute;o (TRC), hệ thống c&acirc;n bằng điện tử (VSC)... Ri&ecirc;ng 2 bản 2.8 c&oacute; th&ecirc;m hệ thống an to&agrave;n&nbsp;Toyota safety sense gồm cảnh b&aacute;o lệch l&agrave;n đường,&nbsp;cảnh b&aacute;o tiền va chạm, điều khiển h&agrave;nh tr&igrave;nh chủ động.</p>\r\n\r\n<p><strong>Động cơ Toyota Fortuner 2021</strong></p>\r\n\r\n<p>Toyota Fortuner 2021 c&oacute; 3 t&ugrave;y chọn động cơ như sau:</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Động cơ diesel 2.4L cho c&ocirc;ng suất 147 m&atilde; lực&nbsp;tại v&ograve;ng tua 3400 v/p v&agrave; m&ocirc; men xoắn 400 Nm tại dải v&ograve;ng tua&nbsp;1600 v/p</p>\r\n	</li>\r\n	<li>\r\n	<p>Động cơ diesel 2.8L&nbsp;cho c&ocirc;ng suất&nbsp;201 m&atilde; lực tại v&ograve;ng tua 3400 v/p v&agrave; m&ocirc; men xoắn 500 Nm tại v&ograve;ng tua&nbsp;1600 v/p</p>\r\n	</li>\r\n	<li>\r\n	<p>Động cơ xăng 2.7L tạm thời chưa được c&ocirc;ng bố c&ocirc;ng suất, nhưng c&oacute; m&ocirc; men xoắn 245&nbsp;Nm tại v&ograve;ng tua&nbsp;4000 v/p.</p>\r\n	</li>\r\n</ul>', 13, 3, '/storage/cars/13/pl0gWxi6FeXRK4EDHwxJ.jpg', 'gia-xe-toyota-1.jpg', 0, '2021-07-17 00:13:40', '2021-07-19 07:35:06', NULL, NULL),
(4, 'Toyota Vios 2017', '2000000', '<p>aaa</p>', 13, 1, '/storage/cars/13/Gmbwyw2eRR4GXh8YjAtm.png', '23.png', 0, '2021-07-18 20:55:03', '2021-07-19 07:24:20', NULL, NULL);

