--
-- Đang đổ dữ liệu cho bảng `tours`
--

INSERT INTO `tours` (`id`, `name`, `price`, `content`, `user_id`, `category_tour_id`, `feature_image_path`, `feature_image_name`, `created_at`, `updated_at`, `deleted_at`, `description`, `departed`, `duration`) VALUES
(1, 'Du lịch Hè - Tour Phú Quốc - Grand World - Checkin Dòng Sông Venice - Vinwonders - Safari từ Sài Gòn', '2000000', '<p>Lịch tr&igrave;nh</p>\r\n\r\n<p>NG&Agrave;Y 1 |&nbsp;TP.HCM &ndash; PH&Uacute; QUỐC (ăn trưa, chiều)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>S&aacute;ng:&nbsp;</strong>&nbsp;Qu&yacute; kh&aacute;ch c&oacute; mặt tại ga quốc nội, s&acirc;n bay T&acirc;n Sơn Nhất trước giờ bay &iacute;t nhất ba tiếng.</p>\r\n\r\n<ul>\r\n	<li>Đại diện c&ocirc;ng ty Du Lịch Việt đ&oacute;n v&agrave; hỗ trợ Qu&yacute; kh&aacute;ch l&agrave;m thủ tục đ&oacute;n chuyến bay đi Ph&uacute; Quốc.&nbsp;</li>\r\n	<li>Đến s&acirc;n bay Ph&uacute; Quốc, Hướng dẫn vi&ecirc;n đ&oacute;n Qu&yacute; kh&aacute;ch tham quan Cơ sở nu&ocirc;i cấy ngọc trai, t&igrave;m hiểu quy tr&igrave;nh nu&ocirc;i cấy v&agrave; Qu&yacute; kh&aacute;ch c&oacute; thể chọn lựa những sản phẩm l&agrave;m đẹp, trang sức l&agrave;m từ ngọc trai để l&agrave;m qu&agrave; cho người th&acirc;n.</li>\r\n	<li>Viếng thăm Ch&ugrave;a Hộ Quốc (hay Thiền Viện Tr&uacute;c L&acirc;m Hộ Quốc), nằm tr&ecirc;n sườn n&uacute;i hướng ra biển ở ph&iacute;a cuối của một con đường uốn kh&uacute;c qua c&aacute;c triền n&uacute;i của đảo Ph&uacute; Quốc, Thiền Viện Tr&uacute;c L&acirc;m Hộ Quốc c&oacute; thể được xem l&agrave; một trong những ng&ocirc;i ch&ugrave;a c&oacute; địa thế đẹp nhất Việt Nam.</li>\r\n	<li>Đo&agrave;n khởi h&agrave;nh đến B&atilde;i Sao, Qu&yacute; kh&aacute;ch tự do tắm biển tại B&atilde;i Sao, b&atilde;i biển c&aacute;t trắng d&agrave;i v&agrave; đẹp nhất đảo, với l&agrave;n nước trong xanh.&nbsp;&nbsp; &nbsp;</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Trưa:&nbsp;</strong>&nbsp;D&ugrave;ng cơm trưa.</p>\r\n\r\n<ul>\r\n	<li>Đo&agrave;n khởi h&agrave;nh ra bến t&agrave;u, Qu&yacute; kh&aacute;ch xuống thuyền du ngoạn cảnh biển, tham gia chương tr&igrave;nh c&acirc;u c&aacute;, tắm biển v&agrave; lặn ngắm thế giới lung linh sắc m&agrave;u của san h&ocirc;.</li>\r\n	<li>Đo&agrave;n tham quan Sunset Sanato Beach Club, nơi được mệnh danh l&agrave; &ldquo;Điểm ngắm ho&agrave;ng h&ocirc;n đẹp nhất đảo Ngọc&rdquo;. tọa lạc ngay trung t&acirc;m B&atilde;i Trường. Kh&ocirc;ng gian biển ở Sunset Sanato Beach Club rất tho&aacute;ng tầm nh&igrave;n v&agrave; được điểm t&ocirc; bằng nhiều c&ocirc;ng tr&igrave;nh kiến tr&uacute;c cực độc đ&aacute;o, mang dấu ấn kh&oacute; phai. V&agrave; c&oacute; lẽ ch&iacute;nh những khối kiến tr&uacute;c k&igrave; lạ v&agrave; đầy s&aacute;ng tạo ấy đ&atilde; gi&uacute;p cho Sunset Sanato Beach Club trở th&agrave;nh điểm check-in si&ecirc;u độc m&agrave; ai đến Ph&uacute; Quốc cũng phải &quot;đỏm d&aacute;ng&quot; l&agrave;m ngay v&agrave;i kiểu.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tối:</strong>&nbsp;D&ugrave;ng cơm tối.&nbsp;&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Nghỉ đ&ecirc;m tại Ph&uacute; Quốc.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>NG&Agrave;Y 2 |&nbsp;GRAND WORLD &ndash; VINWONDER &ndash; H&Ograve;N THƠM (Ăn s&aacute;ng) (Ăn trưa, tối tự t&uacute;c) (Xem th&ecirc;m)</p>\r\n\r\n<p>NG&Agrave;Y 3 |&nbsp;PH&Uacute; QUỐC &ndash; TP.HCM (Ăn s&aacute;ng, trưa) (Xem th&ecirc;m)</p>\r\n\r\n<p>Dịch vụ bao gồm v&agrave; kh&ocirc;ng bao gồm</p>\r\n\r\n<p><em>Giờ bay c&oacute; thể thay đổi theo quy định của h&agrave;ng kh&ocirc;ng. Du Lịch Việt sẽ thay đổi chương tr&igrave;nh cho ph&ugrave; hợp với giờ bay mới nhưng sẽ kh&ocirc;ng chịu tr&aacute;ch nhiệm về c&aacute;c khoản chi ph&iacute; ph&aacute;t sinh</em><br />\r\n<strong>Gi&aacute; tour bao gồm</strong>:<br />\r\n-&nbsp;&nbsp; &nbsp;V&eacute; m&aacute;y bay khứ hồi TP.HCM &ndash; PH&Uacute; QUỐC &ndash; TP.HCM (Vietjet Air)<br />\r\n-&nbsp;&nbsp; &nbsp;Xe tham quan (xe 16 chỗ, 29 chỗ, 35 chỗ, 45 chỗ) t&ugrave;y theo số lượng kh&aacute;ch thực tế tr&ecirc;n chuyến đi.<br />\r\n-&nbsp;&nbsp; &nbsp;Kh&aacute;ch sạn ti&ecirc;u chuẩn đầy đủ tiện nghi 2 kh&aacute;ch người lớn/ ph&ograve;ng. Nếu lẻ người thứ 3, đ&oacute;ng phụ ph&iacute; ph&ograve;ng đơn hoặc ngủ gh&eacute;p ph&ograve;ng 3 kh&aacute;ch.&nbsp;<br />\r\n&nbsp;&nbsp; &nbsp;+ Kh&aacute;ch sạn tương đương 2 sao (loại ph&ograve;ng standard): Galaxy, Lucky, ... hoặc tương đương.<br />\r\n&nbsp;&nbsp; &nbsp;+ Kh&aacute;ch sạn tương đương 3 sao (loại ph&ograve;ng standard): GaiA, Nice life, ... hoặc tương đương.<br />\r\n+ Kh&aacute;ch sạn tương đương 4 sao (loại ph&ograve;ng standard): H&ograve;a B&igrave;nh, ... hoặc tương đương.<br />\r\n+ Kh&aacute;ch sạn tương đương 5 sao (loại ph&ograve;ng standard): Thi&ecirc;n Thanh, ... hoặc tương đương.<br />\r\n-&nbsp;&nbsp; &nbsp;Ăn uống theo chương tr&igrave;nh:&nbsp;<br />\r\n+ Ăn phụ: 2 bữa s&aacute;ng.&nbsp;<br />\r\n+ &nbsp;Ăn ch&iacute;nh: 3 bữa ch&iacute;nh ti&ecirc;u chuẩn 130.000 đồng/bữa.<br />\r\n-&nbsp;&nbsp; &nbsp;V&eacute; tham quan theo chương tr&igrave;nh.&nbsp;<br />\r\n-&nbsp;&nbsp; &nbsp;V&eacute; c&acirc;u c&aacute;, ngắm san h&ocirc;.<br />\r\n-&nbsp;&nbsp; &nbsp;Hướng dẫn vi&ecirc;n tiếng Việt vui vẻ nhiệt t&igrave;nh suốt chuyến đi.<br />\r\n-&nbsp;&nbsp; &nbsp;Bảo hiểm với mức bồi thường tối đa 100.000.000 đồng/trường hợp. Kh&ocirc;ng &aacute;p dụng cho kh&aacute;ch từ 80 tuổi trở l&ecirc;n.<br />\r\n-&nbsp;&nbsp; &nbsp;Qu&agrave; tặng: n&oacute;n du lịch Việt, nước, khăn lạnh.<br />\r\n<strong>Kh&ocirc;ng bao gồm:</strong><br />\r\n-&nbsp;&nbsp; &nbsp;Bia hay nước ngọt trong c&aacute;c bữa ăn.<br />\r\n-&nbsp;&nbsp; &nbsp;V&eacute; tham quan Vinwonder, Safari, Grand World, c&aacute;p treo H&ograve;n Thơm...<br />\r\n-&nbsp;&nbsp; &nbsp;Tham quan ngo&agrave;i chương tr&igrave;nh.<br />\r\n-&nbsp;&nbsp; &nbsp;Chi ph&iacute; c&aacute; nh&acirc;n: điện thoại, giặt ủi&hellip;<br />\r\n-&nbsp;&nbsp; &nbsp;Chi ph&iacute; b&atilde;i biển: d&ugrave;, v&otilde;ng, tắm nước ngọt, chơi tr&ograve; chơi&hellip;<br />\r\n-&nbsp;&nbsp; &nbsp;Thuế VAT.</p>', 13, 1, '/storage/tours/13/KR1hkhKnERPXt7ILQGFD.jpg', 'du-lich-he-grand-world-gia-tot-du-lich-viet.jpg', '2021-07-16 02:57:30', '2021-07-19 06:59:39', NULL, 'Du lịch Hè - Tour Du lịch Phú Quốc - Phú Quốc được xem như ngọc đảo của Việt Nam', '2021-07-25 16:56:00', 3),
(2, 'Tour du thuyền ngắm hoàng hôn sông Sài Gòn', '4000000', '<h2>&nbsp;<strong>TOUR DU THUYỀN NGẮM HOÀNG HÔN SÔNG SÀI GÒN</strong></h2>\r\n\r\n<blockquote>\r\n<ul>\r\n	<li>Thời gian: 1 giờ 30 ph&uacute;t</li>\r\n	<li>Ng&agrave;y khởi h&agrave;nh: Li&ecirc;n hệ</li>\r\n</ul>\r\n</blockquote>\r\n\r\n<p>Tham gia tour du thuyền ngắm ho&agrave;ng h&ocirc;n s&ocirc;ng S&agrave;i G&ograve;n, du kh&aacute;ch sẽ được hướng dẫn l&ecirc;n t&agrave;u, bắt đầu chuyến &quot;Du thuyền ngắm hoàng hôn trên sông Sài Gòn&quot;. H&agrave;nh tr&igrave;nh sẽ đi từ Cảng Sài Gòn ngược dòng đến khu vực t&ograve;a nh&agrave; Landmark rồi vòng lại. Hiện nay, đ&acirc;y được cho là cung đường thủy đẹp nhất Việt Nam.</p>\r\n\r\n<p>Thời gian hoạt động: Tour bắt đầu (xuất bến) mỗi ngày l&uacute;c 17:00. Kết th&uacute;c l&uacute;c 18:30. Đ&acirc;y l&agrave; chuyến đi trải nghiệm với du thuyền Indochina Junk 27 (nếu lượng kh&aacute;ch đ&ocirc;ng b&ecirc;n t&agrave;u sẽ đổi sang t&agrave;u lớn hơn).</p>\r\n\r\n<p>Để bắt đầu chương tr&igrave;nh tour, đo&agrave;n sẽ tập trung tại điểm hẹn: Trung tâm DVHH cảng Sài Gòn số 5 Nguyễn Tất Thành, Quận 4, TP. HCM. T&agrave;u sẽ đ&oacute;n kh&aacute;ch từ 16:30. Sau khi đo&agrave;n tập trung đ&ocirc;ng đủ, t&agrave;u sẽ rời bến l&uacute;c 17:00 v&agrave; bắt đầu h&agrave;nh tr&igrave;nh du ngoạn tr&ecirc;n s&ocirc;ng.&nbsp;</p>\r\n\r\n<p>Trong h&agrave;nh tr&igrave;nh, qu&yacute; kh&aacute;ch sẽ được thưởng thức tr&agrave; / c&agrave; ph&ecirc;/ nước ngọt/ bia v&agrave; thưởng thức v&agrave;i m&oacute;n ăn nhẹ. Đ&acirc;y sẽ l&agrave; khoảng thời gian tuyệt vời để du kh&aacute;ch c&oacute; thể tận hưởng ri&ecirc;ng m&igrave;nh, ngắm nh&igrave;n vẻ đẹp tuyệt vời của S&agrave;i G&ograve;n. Đ&acirc;y cũng l&agrave; l&uacute;c ngắm nh&igrave;n ho&agrave;ng h&ocirc;n tuyệt vời trong ng&agrave;y.</p>\r\n\r\n<p>Dọc đường t&agrave;u di chuyển, du kh&aacute;ch sẽ được tham quan c&aacute;c địa danh tr&ecirc;n s&ocirc;ng. Đối với những ai muốn vui ca th&igrave; c&oacute; thể tham gia h&aacute;t karaoke tr&ecirc;n t&agrave;u.<br />\r\nKhi &aacute;nh ho&agrave;ng h&ocirc;n bu&ocirc;ng xuống tr&ecirc;n s&ocirc;ng, từ đ&acirc;y du kh&aacute;ch sẽ được chi&ecirc;m ngưỡng một vẻ đẹp kỳ lạ của th&agrave;nh phố tuyệt vời.</p>\r\n\r\n<p>Tầm 18:30, t&agrave;u sẽ cập bến, đưa đo&agrave;n trở lại vị tr&iacute; ban đầu. Đo&agrave;n chia tay nhau, kết th&uacute;c chuyến du thuyền ngắm ho&agrave;ng h&ocirc;n s&ocirc;ng S&agrave;i G&ograve;n với nhiều trải nghiệm đ&aacute;ng nhớ.</p>\r\n\r\n<p>&nbsp;BAO GỒM / KH&Ocirc;NG BAO GỒM</p>\r\n\r\n<p><strong>Dịch vụ bao gồm:</strong></p>\r\n\r\n<ul>\r\n	<li>Vé đi tàu</li>\r\n	<li>Bảo hiểm</li>\r\n	<li>1 phần nước uống (Cà phê, nước ngọt, bia.. )</li>\r\n	<li>Đồ ăn nhẹ tr&ecirc;n t&agrave;u để thưởng thức l&uacute;c ngắm ho&agrave;ng h&ocirc;n.</li>\r\n</ul>\r\n\r\n<p><strong>Chưa bao gồm:&nbsp;</strong></p>\r\n\r\n<ul>\r\n	<li>C&aacute;c chi ph&iacute; ph&aacute;t sinh trong qu&aacute; tr&igrave;nh tham gia tour</li>\r\n	<li>VAT</li>\r\n</ul>', 13, 3, '/storage/tours/13/BB62ysllmkx0RwtSlUhP.jpg', 'sai-gan-hoang-hon.jpg', '2021-07-16 03:00:22', '2021-07-19 07:00:49', NULL, 'Nếu bạn ở TP. HCM hoặc vừa mới đặt chân tới khám phá TP. HCM? Nếu bạn đang muốn đổi gió để ngắm nhìn thành phố ở một góc độ khác, tour du thuyền ngắm hoàng hôn trên sông Sài Gòn là một cơ hội lý tưởng. Đây là dịp để du khách:', '2021-07-24 16:58:00', 6),
(3, 'Đà  Nẵng 3 ngày 2 đêm', '2000000', '<p>Use the tab and enter or arrow keys to move between tabs</p>\r\n\r\n<ul>\r\n	<li>Lịch tr&igrave;nh tour</li>\r\n	<li>Bảng gi&aacute;</li>\r\n	<li>Ảnh tour</li>\r\n	<li>Đặt tour</li>\r\n</ul>\r\n\r\n<p>LỊCH TR&Igrave;NH TOUR</p>\r\n\r\n<p><strong>NG&Agrave;Y 01: Đ&Oacute;N KH&Aacute;CH &ndash; SƠN TR&Agrave; &ndash; MỸ KH&Ecirc; &ndash; SUNWHEEL (Ăn trưa, chiều) Thứ Năm &ndash; Thứ Bảy</strong><br />\r\n<strong>S&aacute;ng:</strong>&nbsp;Đ&oacute;n qu&yacute; kh&aacute;ch tại Đ&agrave; Nẵng (S&acirc;n bay, Ga, điểm hẹn trung t&acirc;m) từ 07h00 đến 13h00 (sau thời gian n&agrave;y, qu&yacute; kh&aacute;ch tự t&uacute;c nhập đo&agrave;n) xe đưa Qu&yacute; kh&aacute;ch đi ăn trưa với đặc sản nổi tiếng Đ&agrave; Nẵng &ldquo;B&aacute;nh tr&aacute;ng thịt heo 2 đầu da &amp; Mỳ Quảng&rdquo;. Nhận ph&ograve;ng Kh&aacute;ch sạn 3* nghỉ ngơi.<br />\r\n<strong>Chiều:</strong>&nbsp;Khởi h&agrave;nh đi B&aacute;n Đảo Sơn Tr&agrave; mục k&iacute;ch phố biển Đ&agrave; Nẵng tr&ecirc;n cao, viếng Linh Ứng Tự &ndash; nơi c&oacute; tượng Phật B&agrave; 67m cao nhất Việt Nam v&agrave; tắm biển Mỹ Kh&ecirc; Đ&agrave; Nẵng.<br />\r\n<strong>Tối:</strong>&nbsp;Ăn tối nh&agrave; h&agrave;ng. Tự do trải nghiệm C&ocirc;ng vi&ecirc;n Ch&acirc;u &Aacute; &ndash; Asia Park Đ&agrave; Nẵng l&agrave; sự kết hợp những n&eacute;t độc đ&aacute;o, mới lạ của c&aacute;c m&ocirc; h&igrave;nh giải tr&iacute; tr&ecirc;n thế giới với những n&eacute;t văn h&oacute;a đặc sắc đậm chất &Aacute; Đ&ocirc;ng.</p>\r\n\r\n<p><a href=\"https://www.tourismdanang.vn/wp-content/uploads/Phat-Ba-Quan-Am1.jpg\"><img alt=\"Phat Ba Quan Am\" src=\"https://www.tourismdanang.vn/wp-content/uploads/Phat-Ba-Quan-Am1.jpg\" style=\"height:454px; width:680px\" /></a></p>\r\n\r\n<p>Tượng Phật B&agrave; Quan &Acirc;m tại Ch&ugrave;a Linh Ứng</p>\r\n\r\n<p><strong>NG&Agrave;Y 02: RỪNG DỪA BẢY MẪU &ndash; PHỐ CỔ HỘI AN (Ăn s&aacute;ng, trưa, tối) Thứ S&aacute;u &ndash; Chủ Nhật</strong><br />\r\n<strong>S&aacute;ng:</strong>&nbsp;Điểm t&acirc;m. Khởi h&agrave;nh v&agrave;o Hội An tham quan Rừng Dừa Bảy Mẫu. Kh&aacute;ch đi bộ dọc theo con đường l&agrave;ng 5 ph&uacute;t đến bến thuyền. Kh&aacute;ch tham quan Rừng dừa Bảy Mẫu tr&ecirc;n những chiếc thuyền th&uacute;ng một phương tiện đặc trưng của l&agrave;ng ch&agrave;i th&ocirc;n Vạn Lăng. Qu&yacute; kh&aacute;ch được tận mắt xem người d&acirc;n địa phương quăng ch&agrave;i tr&ecirc;n s&ocirc;ng để đ&aacute;nh bắt c&aacute; &amp; c&oacute; thể tự tay trải nghiệm kỹ năng quăng ch&agrave;i của m&igrave;nh. Sau đ&oacute;, xem m&agrave;n lắc<br />\r\nth&uacute;ng ch&eacute;m nước đặc sắc của nghệ nh&acirc;n lắc th&uacute;ng của th&ocirc;n Vạn Lăng.<br />\r\nĐo&agrave;n th&uacute;ng cập bến Nh&agrave; h&agrave;ng sinh th&aacute;i, nghỉ ngơi tr&ecirc;n nh&agrave; l&aacute;. Qu&yacute; kh&aacute;ch tham quan tự do<br />\r\nkh&ocirc;ng gian khu du lịch sinh th&aacute;i, sau đ&oacute; tập kết tham gia tr&ograve; chơi d&acirc;n gian c&oacute; thưởng vui nhộn: Đi cầu khỉ, đập ni&ecirc;u, nhảy bao bố, chui hầm,&hellip;<br />\r\n<strong>Trưa:</strong>&nbsp;D&ugrave;ng cơm trưa n&oacute;ng với hương vị đặc trưng l&agrave;ng qu&ecirc;. Tự do nghỉ ngơi tại nh&agrave; h&agrave;ng sinh th&aacute;i.<br />\r\n<strong>Chiều:</strong>&nbsp;Chia tay khu du lịch, khởi h&agrave;nh qua Hội An b&aacute;ch bộ tham quan v&agrave; mua sắm Phố Cổ với: Ch&ugrave;a Cầu Nhật Bản, Nh&agrave; Cổ h&agrave;ng trăm năm tuổi, Hội Qu&aacute;n Phước Kiến &amp; Xưởng thủ c&ocirc;ng mỹ nghệ. Trở về Đ&agrave; Nẵng theo con đường biển mang t&ecirc;n Đại tướng V&otilde; Nguy&ecirc;n Gi&aacute;p, ngắm nh&igrave;n Ngũ H&agrave;nh Sơn.<br />\r\n<strong>Tối:</strong>&nbsp;Ăn tối Nh&agrave; h&agrave;ng. Tự do kh&aacute;m Đ&agrave; Th&agrave;nh về đ&ecirc;m. Ngủ Kh&aacute;ch sạn 3*.</p>\r\n\r\n<p><a href=\"http://www.tourismdanang.vn/wp-content/uploads/Rung-bay-mau-hoi-an.jpg\"><img alt=\"\" src=\"http://www.tourismdanang.vn/wp-content/uploads/Rung-bay-mau-hoi-an.jpg\" style=\"height:auto; width:680px\" /></a></p>\r\n\r\n<p><br />\r\nRừng Dừa Bảy Mẫu &ndash; Hội An</p>\r\n\r\n<p><strong>NG&Agrave;Y 03: B&Agrave; N&Agrave; N&Uacute;I CH&Uacute;A &ndash; TẮM BIỂN MỸ KH&Ecirc; (Ăn s&aacute;ng, trưa) Thứ Bảy &ndash; Thứ Hai</strong><br />\r\n<strong>S&aacute;ng:</strong>&nbsp;Điểm t&acirc;m. Khởi h&agrave;nh đi tham quan B&agrave; N&agrave; &ndash; N&uacute;i Ch&uacute;a (V&eacute; c&aacute;p treo B&agrave; N&agrave; tự t&uacute;c), nơi c&oacute; những khoảnh khắc giao m&ugrave;a bất ngờ Xu&acirc;n &ndash; Hạ &ndash; Thu &ndash; Đ&ocirc;ng trong một ng&agrave;y. Q&uacute;y kh&aacute;ch sẽ tận hưởng cảm gi&aacute;c bồng bền khi ngồi tr&ecirc;n Cabin lơ lững giữa chừng m&acirc;y. Viếng ch&ugrave;a Linh Ứng với tượng Phật Th&iacute;ch Ca cao 27m, viếng đền thờ B&agrave; Ch&uacute;a Mẫu Thượng Ng&agrave;n. Tham quan Cầu v&agrave;ng &ndash; &ldquo;HOT&rdquo; &ndash; C&acirc;y cầu độc đ&aacute;o nằm trong vườn Thi&ecirc;n Thai ở B&agrave; N&agrave; Hill &ndash; được x&acirc;y dựng tr&ecirc;n độ cao 1.400m so với mặt nước biển, c&acirc;y cầu gồm 8 nhịp v&agrave; c&oacute; độ d&agrave;i l&agrave; 150m. C&acirc;y cầu được n&acirc;ng đỡ bởi đ&ocirc;i b&agrave;n tay khổng lồ. Người đi như lạc giữa ti&ecirc;n cảnh nơi c&oacute; m&acirc;y, trời bao la.<br />\r\n<strong>Trưa:</strong>&nbsp;Ăn trưa nh&agrave; h&agrave;ng buffet B&agrave; N&agrave;. (Nếu kh&ocirc;ng ăn trưa B&agrave; N&agrave; chỉ giảm 120.000 vnđ/kh&aacute;ch)<br />\r\nTiếp tục tham gia vui chơi tại c&ocirc;ng vi&ecirc;n Fantasy Park: với c&aacute;c tr&ograve; chơi phi&ecirc;u lưu mới lạ như: V&ograve;ng Quay T&igrave;nh Y&ecirc;u, Phi C&ocirc;ng Skiver, Đường Đua Lửa, Ng&ocirc;i Nh&agrave; Ma v&agrave; Khu trưng b&agrave;y hơn 40 tượng s&aacute;p những nh&acirc;n vật nổi tiếng tr&ecirc;n thế giới&hellip;<br />\r\n<strong>Chiều:</strong>&nbsp;Khởi h&agrave;nh về lại trung t&acirc;m th&agrave;nh phố. Tiễn qu&yacute; kh&aacute;ch tại s&acirc;n bay Đ&agrave; Nẵng (c&aacute;c chuyến sau 18h30). Chia tay Qu&yacute; kh&aacute;ch v&agrave; kết th&uacute;c chương tr&igrave;nh. Hẹn gặp lại qu&yacute; kh&aacute;ch!</p>\r\n\r\n<p><a href=\"https://www.tourismdanang.vn/wp-content/uploads/Pho-Co-Hoi-An-61.jpg\"><img alt=\"Pho Co Hoi An\" src=\"https://www.tourismdanang.vn/wp-content/uploads/Pho-Co-Hoi-An-61.jpg\" style=\"height:472px; width:720px\" /></a></p>\r\n\r\n<p>Phố Cổ Hội An</p>', 13, 7, '/storage/tours/13/Y4s2mmxsI5hi6vn0Axuq.jpg', '341925490765232b92601o-908.jpg', '2021-07-17 00:09:43', '2021-07-19 07:02:36', NULL, 'Mô tả Đà  Nẵng 3 ngày 2 đêm', '2021-07-24 14:09:00', 3),
(4, 'Tour Đà Lạt- Cần Thơ- Mỹ Tho 3N3Đ', '3000000', '<p>Tour Đ&agrave; Lạt- Cần Thơ- Mỹ Tho 3N3Đ l&agrave; chương tr&igrave;nh tour được đ&ocirc;ng đảo du kh&aacute;ch quan t&acirc;m nhất. Đến đ&acirc;y du kh&aacute;ch sẽ được trải nghiệm tham quan khu du lịch Mỹ Kh&aacute;nh, Nh&agrave; Cổ B&igrave;nh Thủy, Thiền Viện Phương Nam, Chợ Nổi C&aacute;i Răng,...</p>\r\n\r\n<h2><strong>Lịch tr&igrave;nh chi tiết tour&nbsp;Đ&agrave; Lạt- Cần Thơ- Mỹ Tho 3N3Đ</strong></h2>\r\n\r\n<ul>\r\n	<li>Thời gian: 3 ng&agrave;y 3 đ&ecirc;m</li>\r\n	<li>Phương tiện: &Ocirc; t&ocirc; đời mới&nbsp;</li>\r\n</ul>\r\n\r\n<h2><strong>Đ&Ecirc;M 1: Đ&Agrave; LẠT - CẦN THƠ</strong></h2>\r\n\r\n<p>20h30: Tổ phục vụ c&ocirc;ng ty Thi&ecirc;n Nh&acirc;n Travel c&oacute; mặt l&agrave;m quen với Trưởng Đo&agrave;n v&agrave; Qu&yacute; Kh&aacute;ch tại điểm hẹn &ndash; Đ&Agrave; LẠT.Đo&agrave;n khởi h&agrave;nh đi Cần Thơ.<a href=\"https://123tadi.com/tour-gia-re/tour-trong-nuoc/tour-du-lich-da-lat-can-tho-my-tho-3n3d/\"><img alt=\"Cần Thơ\" src=\"https://123tadi.com/wp-content/uploads/2020/02/Can-tho1.jpg\" style=\"height:400px; width:700px\" /></a>Cần Thơ- Tour Đ&agrave; Lạt Cần Thơ Mỹ Tho 3n2đ<a href=\"https://123tadi.com/tour-gia-re/tour-trong-nuoc/tour-du-lich-da-lat-mong-mo-3-ngay-2-dem/\">TOUR DU LỊCH Đ&Agrave; LẠT MỘNG MƠ 3 NG&Agrave;Y 2 Đ&Ecirc;M</a>Du kh&aacute;ch được nghe HDV giới thiệu v&agrave; thuyết minh về c&aacute;c danh lam thắng cảnh tr&ecirc;n đường đi.Nghỉ đ&ecirc;m, sinh hoạt tr&ecirc;n xe.Đo&agrave;n dừng ch&acirc;n nghỉ ngơi vệ sinh c&aacute; nh&acirc;n.<strong>NG&Agrave;Y 01: KHU DL MỸ KH&Aacute;NH &ndash; NH&Agrave; CỔ B&Igrave;NH THỦY- DU THUYỀN S&Ocirc;NG HẬU- ĐỜN CA T&Agrave;I TỬ (Ăn s&aacute;ng &ndash; trưa &ndash; tối):</strong>06h30: Tới Cần Thơ du kh&aacute;ch vệ sinh c&aacute; nh&acirc;n &ndash; d&ugrave;ng điểm t&acirc;m s&aacute;ng.</p>\r\n\r\n<ul>\r\n	<li>Khởi h&agrave;nh tham quan khu du lịch Mỹ Kh&aacute;nh:</li>\r\n</ul>\r\n\r\n<p><a href=\"https://123tadi.com/tour-gia-re/tour-trong-nuoc/tour-du-lich-da-lat-can-tho-my-tho-3n3d/\"><img alt=\"khu du lịch Mỹ Khánh\" src=\"https://123tadi.com/wp-content/uploads/2020/02/khu-du-lich-My-Khanh.jpg\" style=\"height:400px; width:700px\" /></a>khu du lịch Mỹ Kh&aacute;nh- Tour Đ&agrave; Lạt Cần Thơ Mỹ Tho 3n2đL&agrave;ng du lịch Mỹ Kh&aacute;nh l&agrave; một điểm du lịch sinh th&aacute;i kh&ocirc;ng chỉ nổi tiếng của Tp.&nbsp;Mỹ Kh&aacute;nh hấp dẫn du kh&aacute;ch với những vườn c&acirc;y tr&aacute;i trĩu nặng, những ng&ocirc;i nh&agrave; cổ Nam Bộ, b&ecirc;n cạnh l&agrave; những ao c&aacute;, k&ecirc;nh rạch c&ugrave;ng nhiều hoạt động du lịch th&uacute; vị kh&aacute;c.&nbsp;<a href=\"https://123tadi.com/dia-diem-du-lich/lac-tien-gioi-toa-do-song-ao-cuc-chil/\">LẠC TI&Ecirc;N GIỚI TỌA ĐỘ SỐNG ẢO CỰC CHILL GIỮA L&Ograve;NG Đ&Agrave; LẠT</a></p>\r\n\r\n<ul>\r\n	<li>Du kh&aacute;ch d&ugrave;ng cơm trưa</li>\r\n</ul>\r\n\r\n<p>Sau đ&oacute; xe đưa du Kh&aacute;ch về nhận ph&ograve;ng kh&aacute;ch sạn nghỉ ngơi.</p>\r\n\r\n<ul>\r\n	<li>Buổi Chiều Xe đưa đo&agrave;n tham quan: Nh&agrave; Cổ B&igrave;nh Thủy; Khu phố thị trung t&acirc;m TP.Cần Thơ, dạo bến Ninh Kiều, mua sắm ngay tại ng&ocirc;i chợ mới Cần Thơ.</li>\r\n</ul>\r\n\r\n<p><a href=\"https://123tadi.com/tour-gia-re/tour-trong-nuoc/tour-du-lich-da-lat-can-tho-my-tho-3n3d/\"><img alt=\"Nhà Cổ Bình Thủy\" src=\"https://123tadi.com/wp-content/uploads/2020/02/Nha-Co-Binh-Thuy.jpg\" style=\"height:400px; width:700px\" /></a>Nh&agrave; Cổ B&igrave;nh Thủy- Tour Đ&agrave; Lạt Cần Thơ Mỹ Tho 3n2đ</p>\r\n\r\n<ul>\r\n	<li>Tiếp tục h&agrave;nh tr&igrave;nh đo&agrave;n tham quan Thiền Viện Phương Nam một trong những c&ocirc;ng tr&igrave;nh Phật gi&aacute;o lớn nhất ở T&acirc;y Nam Bộ. Đến với nơi đ&acirc;y, con người ta như được trở về với những điều thanh b&igrave;nh nhất, giản dị nhất. Với những ai muốn t&igrave;m tới một chốn t&acirc;m linh thanh tịnh.</li>\r\n</ul>\r\n\r\n<p><a href=\"https://123tadi.com/dia-diem-du-lich/bac-thang-len-hoi-ong-troi-diem-check/\">BẮC THANG L&Ecirc;N HỎI &Ocirc;NG TRỜI ĐIỂM CHECK IN TUYỆT ĐẸP TẠI Đ&Agrave; LẠT</a>D&ugrave;ng cơm tối tr&ecirc;n du thuyền Cần Thơ, nghe đờn ca t&agrave;i tử - c&ugrave;ng giao lưu văn nghệ với c&aacute;c nghệ sĩ.Về kh&aacute;ch sạn nghỉ ngơi hoặc tự do dạo phố T&acirc;y Đ&ocirc; về đ&ecirc;m.<strong>NG&Agrave;Y 2: CẦN THƠ &ndash; CHỢ NỔI C&Aacute;I RĂNG - MỸ THO &ndash; C&Ugrave; LAO LONG L&Acirc;N QUY PHỤNG &ndash; T&Aacute;T MƯƠNG BẮT C&Aacute; - CH&Ugrave;A VĨNH TR&Agrave;NG (Ăn s&aacute;ng, trưa, tối)</strong>05h30 Đo&agrave;n ra Bến Ninh Kiều đi thuyền tham quan khu Chợ Nổi C&aacute;i Răng, t&igrave;m hiểu n&eacute;t đẹp văn h&oacute;a miền s&ocirc;ng nước Cửu Long.&nbsp;Du kh&aacute;ch tự do mua sắm đặc sản tr&ecirc;n c&aacute;c &ldquo;Bẹo H&agrave;ng&rdquo;(Ghe n&agrave;o b&aacute;n đặc sản g&igrave; th&igrave; treo l&ecirc;n 1 c&acirc;y s&agrave;o ngang trước ghe)<a href=\"https://123tadi.com/tour-gia-re/tour-trong-nuoc/tour-du-lich-da-lat-can-tho-my-tho-3n3d/\"><img alt=\"Bến Ninh Kiều\" src=\"https://123tadi.com/wp-content/uploads/2020/02/B%E1%BA%BFn-Ninh-Ki%E1%BB%81u.jpg\" style=\"height:400px; width:700px\" /></a>Bến Ninh Kiều- Tour Đ&agrave; Lạt Cần Thơ Mỹ Tho 3n2đSau đ&oacute; quay trở về kh&aacute;ch sạn d&ugrave;ng điểm t&acirc;m tại nh&agrave; h&agrave;ng.&nbsp;Trả ph&ograve;ng khởi h&agrave;nh về Mỹ ThoTới Mỹ Tho đo&agrave;n đến bến t&agrave;u 30-04, L&agrave;m thủ tục l&ecirc;n t&agrave;u - Qu&yacute; kh&aacute;ch tham gia chuyến h&agrave;nh tr&igrave;nh qua bốn cụm C&ugrave; Lao Long &ndash; L&acirc;n &ndash; Quy &ndash; Phụng.<a href=\"https://123tadi.com/dia-diem-du-lich/ca-phe-xu-so-than-tien-o-da-lat-2020/\">C&Agrave; PH&Ecirc; XỨ SỞ THẦN TI&Ecirc;N ĐIỂM SỐNG ẢO CỰC CHẤT Ở Đ&Agrave; LẠT 2020</a>Qu&yacute; kh&aacute;ch d&ugrave;ng cơm trưa tại KDL Cồn Thới Sơn.<a href=\"https://123tadi.com/tour-gia-re/tour-trong-nuoc/tour-du-lich-da-lat-can-tho-my-tho-3n3d/\"><img alt=\"KDL Cồn Thới Sơn\" src=\"https://123tadi.com/wp-content/uploads/2020/02/KDL-C%E1%BB%93n-Th%E1%BB%9Bi-S%C6%A1n.jpg\" style=\"height:400px; width:700px\" /></a>KDL Cồn Thới Sơn- Tour Đ&agrave; Lạt Cần Thơ Mỹ Tho 3n2đĐo&agrave;n gh&eacute; tham quan KDL Đạo Dừa &ndash; nghe thuyết minh về lịch sử Đạo Dừa v&agrave; chi&ecirc;m ngưỡng kiến tr&uacute;c lạ mắt nổi tiếng tr&ecirc;n đất Bến Tre. Tự do thưởng thức tr&agrave; &ndash; Mật ong, thăm l&ograve; kẹo dừa thưởng thức đặc sản Bến Tre (miễn ph&iacute;).Tạm biệt Bến Tre, t&agrave;u du lịch đưa qu&yacute; kh&aacute;ch đến Cồn Quy. Đo&agrave;n tự do tản bộ - tham quan vườn tr&aacute;i c&acirc;y v&agrave; cơ sở sản xuất h&agrave;ng thủ c&ocirc;ng mỹ nghệ từ C&acirc;y Dừa.<a href=\"https://123tadi.com/tour-gia-re/tour-trong-nuoc/tour-du-lich-da-lat-can-tho-my-tho-3n3d/\"><img alt=\"Cồn Quy\" src=\"https://123tadi.com/wp-content/uploads/2020/02/C%E1%BB%93n-Quy.jpg\" style=\"height:400px; width:700px\" /></a>Cồn Quy- Tour Đ&agrave; Lạt Cần Thơ Mỹ Tho 3n2đQu&yacute; kh&aacute;ch tự do kh&aacute;m ph&aacute; v&agrave; thưởng thức tr&aacute;i c&acirc;y theo m&ugrave;a &ldquo;miễn ph&iacute;&rdquo; v&agrave; giao lưu Đờn Ca T&agrave;i Tử c&ugrave;ng c&aacute;c nghệ sĩ tại KDL.T&agrave;u du lịch đưa du kh&aacute;ch đến Cồn Thới Sơn. Qu&yacute; kh&aacute;ch nh&igrave;n ngắm cầu Rạch Miễu nối liền 2 tỉnh Tiền Giang v&agrave; Bến Tre.Qu&yacute; kh&aacute;ch h&oacute;a th&acirc;n th&agrave;nh c&aacute;c nghệ sỹ Miền T&acirc;y bằng h&agrave;nh tr&igrave;nh xuy&ecirc;n qua c&aacute;c k&ecirc;nh rạch bằng xuồng Ba L&aacute; c&ugrave;ng c&aacute;c thiếu nữ.<a href=\"https://123tadi.com/dia-diem-du-lich/quan-ca-phe-kombi-land-da-lat/\">KOMBI LAND Đ&Agrave; LẠT ĐIỂM ĐẾN L&Yacute; TƯỞNG TRONG DỊP VALENTINE 14/2</a>Tự do ngắm cảnh v&agrave; chụp h&igrave;nh lưu niệm. Thưởng thức bữa cơm trưa với c&aacute;c m&oacute;n ăn miền s&ocirc;ng nước + C&aacute; tai tương chi&ecirc;n x&ugrave; + đặc sản X&ocirc;i Phồng + cuốn b&aacute;nh tr&aacute;ng&hellip;Chương tr&igrave;nh đặc biệt d&agrave;nh cho Qu&yacute; Du Kh&aacute;ch: Qu&yacute; Vị Trực tiếp H&oacute;a Th&acirc;n th&agrave;nh những người d&acirc;n Đồng Bằng S&ocirc;ng Cửu Long tham gia &ldquo;t&aacute;t mương bắt c&aacute;&rdquo;.&nbsp;Tự Tay chế biến v&agrave; Thưởng thức c&aacute; l&oacute;c nướng ngay tại Khu vườn nh&agrave; d&acirc;n.Kết th&uacute;c chương tr&igrave;nh Đo&agrave;n trở về bến t&agrave;u, T&agrave;u đưa du kh&aacute;ch về lại đất liền.Tiếp tục h&agrave;nh tr&igrave;nh xe đưa đo&agrave;n tham quan Ch&ugrave;a Vĩnh Tr&agrave;ngNg&ocirc;i ch&ugrave;a nổi tiếng với lối kiến tr&uacute;c độc đ&aacute;o, kết hợp h&agrave;i h&ograve;a giữa kiến tr&uacute;c &Aacute; &ndash; &Acirc;u cổ k&iacute;nh.<a href=\"https://123tadi.com/tour-gia-re/tour-trong-nuoc/tour-du-lich-da-lat-can-tho-my-tho-3n3d/\"><img alt=\"Chùa Vĩnh Tràng\" src=\"https://123tadi.com/wp-content/uploads/2020/02/Ch%C3%B9a-V%C4%A9nh-Tr%C3%A0ng.jpg\" style=\"height:400px; width:700px\" /></a>Ch&ugrave;a Vĩnh Tr&agrave;ng- Tour Đ&agrave; Lạt Cần Thơ Mỹ Tho 3n2đDu kh&aacute;ch d&ugrave;ng cơm tối. Về kh&aacute;ch sạn nghỉ ngơi. Tự do kh&aacute;m ph&aacute; chợ đ&ecirc;m v&agrave; ẩm thực Mỹ Tho về đ&ecirc;m.<strong>NG&Agrave;Y 3: MỸ THO &ndash; Đ&Agrave; LẠT (Ăn s&aacute;ng, trưa)</strong>07h30: Qu&yacute; kh&aacute;ch d&ugrave;ng điểm t&acirc;m s&aacute;ng. L&agrave;m thủ tục trả ph&ograve;ng về Đ&agrave; Lạt.Tr&ecirc;n đường về du kh&aacute;ch c&oacute; thể mua sắm đặc sản S&ocirc;ng nước miệt vườn Cửu long B&aacute;nh P&iacute;a S&oacute;c Trăng,Nem Lai Vung<a href=\"https://123tadi.com/tour-gia-re/tour-trong-nuoc/tour-du-lich-da-lat-can-tho-my-tho-3n3d/\"><img alt=\"Bánh Pía Sóc Trăng\" src=\"https://123tadi.com/wp-content/uploads/2020/02/B%C3%A1nh-P%C3%ADa-S%C3%B3c-Tr%C4%83ng.jpg\" style=\"height:400px; width:700px\" /></a>B&aacute;nh P&iacute;a S&oacute;c Trăng- Tour Đ&agrave; Lạt Cần Thơ Mỹ Tho 3n2đH&agrave;nh tr&igrave;nh về lại Đ&agrave; LạtD&ugrave;ng cơm Trưa.Về đến Đ&agrave; Lạt chia tay qu&yacute; kh&aacute;ch.Thi&ecirc;n Nh&acirc;n Travel ch&acirc;n th&agrave;nh c&aacute;m ơn sự quan t&acirc;m v&agrave; hợp t&aacute;c của q&uacute;y vị.&nbsp;Ch&uacute;c Qu&yacute; Vị sức khỏe, hạnh ph&uacute;c v&agrave; thịnh vượng.<a href=\"http://thiennhantravel.com/page/tours/detail/tour-city-da-lat-7\">TOUR CITY Đ&Agrave; LẠT</a></p>\r\n\r\n<p><strong><em>Kết Th&uacute;c Chương tr&igrave;nh tour du lịch Đ&agrave; Lạt - Cần Thơ - Mỹ Tho 3N3Đ</em></strong></p>\r\n\r\n<p>NOTE: **V&igrave; sự thuận tiện, an to&agrave;n cho kh&aacute;ch h&agrave;ng c&ocirc;ng ty sẽ giữ quyền thay đổi lộ tr&igrave;nh nếu bất ngờ xảy ra c&aacute;c trường hợp kh&aacute;ch quan như thi&ecirc;n tai, khủng bố hoặc do c&oacute; sự cố, sự thay đổi lịch tr&igrave;nh của phương tiện vận chuyển c&ocirc;ng cộng như t&agrave;u hỏa, m&aacute;y bay,...**.</p>\r\n\r\n<h2><strong>Gi&aacute; tour du lịch Đ&agrave; Lạt - Cần Thơ - Mỹ Tho 3N3Đ:</strong></h2>\r\n\r\n<p><a href=\"https://123tadi.com/tour-gia-re/tour-trong-nuoc/tour-du-lich-da-lat-can-tho-my-tho-3n3d/\"><img alt=\"Giá tour du lịch Đà Lạt - Cần Thơ - Mỹ Tho 3N3Đ\" src=\"https://123tadi.com/wp-content/uploads/2020/02/Capture-3.png\" style=\"height:113px; width:585px\" /></a>Gi&aacute; tour Đ&agrave; Lạt Cần Thơ&nbsp; Mỹ Tho 3N3Đ</p>\r\n\r\n<h2><strong>GI&Aacute; TOUR DU LỊCH Đ&Agrave; LẠT - CẦN THƠ - MỸ THO 3N3Đ BAO GỒM:</strong></h2>\r\n\r\n<ul>\r\n	<li>Xe du lịch đời mới phục vụ suốt tuyến.</li>\r\n	<li>Hướng dẫn vi&ecirc;n nhiệt t&igrave;nh, vui vẻ phục vụ suốt chuyến đi.</li>\r\n	<li>C&aacute;c bữa ăn theo chương tr&igrave;nh:</li>\r\n	<li>Ăn s&aacute;ng 03 bữa: 01 bữa T&ocirc;+Ly - 02 Buffet&nbsp;Ăn ch&iacute;nh 05 bữa. 150.000đ/suất.C&aacute;c bữa ăn được thay đổi nh&agrave; h&agrave;ng kh&aacute;c nhau để du kh&aacute;ch kh&ocirc;ng nh&agrave;m ch&aacute;n.</li>\r\n	<li>V&eacute; tham quan v&agrave;o cửa c&aacute;c thắng cảnh.</li>\r\n	<li>Nước suối tr&ecirc;n đường</li>\r\n	<li>Bảo hiểm du lịch theo qui định bảo hiểm Việt Nam.</li>\r\n	<li>Tặng phẩm : 01 mũ du lịch Thi&ecirc;n Nh&acirc;n.</li>\r\n</ul>\r\n\r\n<h2><strong>GI&Aacute; TOUR DU LỊCH Đ&Agrave; LẠT - CẦN THƠ - MỸ THO 3N3Đ KH&Ocirc;NG BAO GỒM:</strong></h2>\r\n\r\n<ul>\r\n	<li>C&aacute;c chương tr&igrave;nh vui chơi giải tr&iacute; c&aacute; nh&acirc;n, c&aacute;c chi ph&iacute; ăn uống ngo&agrave;i chương tr&igrave;nh.</li>\r\n	<li>Vat 10%</li>\r\n</ul>\r\n\r\n<h2><strong>GI&Aacute; TOUR CHO TRẺ EM:</strong></h2>\r\n\r\n<ul>\r\n	<li>Trẻ em dưới 04 tuổi : miễn gi&aacute; tour (cha mẹ tự lo cho b&eacute;)</li>\r\n	<li>Trẻ em từ 05 &ndash; 10 tuổi: 50% gi&aacute; tour, bao gồm c&aacute;c dịch vụ ăn uống, ghế ngồi tr&ecirc;n xe nhưng b&eacute; ngủ chung với cha mẹ, kh&ocirc;ng c&oacute; giường ri&ecirc;ng, trẻ thứ 2 phụ thu 75% gi&aacute; v&eacute;.</li>\r\n	<li>Trẻ em từ 11 tuổi trở l&ecirc;n: 100% gi&aacute; tour như người lớn.</li>\r\n	<li>Kh&aacute;ch đi tour k&egrave;m theo từ 02 trẻ em trở l&ecirc;n dưới 06 tuổi: vui l&ograve;ng mua th&ecirc;m 1 v&eacute; 50% gi&aacute; tour để c&oacute; ti&ecirc;u chuẩn ăn uống, ghế ngồi, tham quan cho c&aacute;c b&eacute;</li>\r\n</ul>\r\n\r\n<p><strong>ĐIỀU KIỆN THANH TO&Aacute;N:</strong></p>\r\n\r\n<ul>\r\n	<li>Thanh to&aacute;n 80% tiền tour khi k&yacute; Hợp Đồng.</li>\r\n	<li>20% thanh to&aacute;n trước 10 ng&agrave;y khởi h&agrave;nh.</li>\r\n	<li>Sau 3 ng&agrave;y thanh to&aacute;n ph&aacute;t sinh c&ograve;n lại của chi ph&iacute; tour (Nếu c&oacute;).</li>\r\n</ul>', 13, 3, '/storage/tours/13/vGcuusTle2PWNW6W7bjQ.jpg', 'Can-tho.jpg', '2021-07-19 07:09:32', '2021-07-19 07:09:32', NULL, 'Tour Đà Lạt- Cần Thơ- Mỹ Tho 3N3Đ là chương trình tour được đông đảo du khách quan tâm nhất. Đến đây du khách sẽ được trải nghiệm tham quan khu du lịch Mỹ Khánh, Nhà Cổ Bình Thủy,...', '2021-07-29 22:08:00', 3),
(5, 'Tour du lịch Hòn Sơn 2 ngày 2 đêm', '1980000', '<p><strong>Tour du lịch H&ograve;n Sơn 2 ng&agrave;y 2 đ&ecirc;m l&agrave; một h&agrave;nh tr&igrave;nh th&uacute; vị để kh&aacute;m ph&aacute; vẻ đẹp hoang sơ của thi&ecirc;n nhi&ecirc;n H&ograve;n Sơn. Với chuyến đi n&agrave;y, du kh&aacute;ch sẽ c&oacute; cơ hội kh&aacute;m ph&aacute; xuy&ecirc;n đảo, check in c&aacute;c địa điểm tuyệt đẹp, chinh phục con đường uốn cong theo sườn n&uacute;i... Đ&acirc;y cũng l&agrave; cơ hội để thưởng thức hải sản tươi ngon của h&ograve;n đảo n&agrave;y.</strong></p>\r\n\r\n<p><img alt=\"Tour du lịch Hòn Sơn 2 ngày 2 đêm\" src=\"https://trippy.vn/storage/tour/viet-nam/mien-tay/tour-du-lich-hon-son-2-ngay-2-dem-5.jpg\" /></p>\r\n\r\n<h3>Đ&ecirc;m ng&agrave;y 1: S&agrave;i G&ograve;n - nghỉ đ&ecirc;m tr&ecirc;n xe</h3>\r\n\r\n<p>V&agrave;o ng&agrave;y đầu ti&ecirc;n của chuyến đi, theo giờ hẹn (buổi tối, 22:00), du kh&aacute;ch c&oacute; mặt tại điểm đ&oacute;n ở TP. HCM. Sau khi l&ecirc;n xe, đo&agrave;n thẳng tiến về Rạch Gi&aacute;. Du kh&aacute;ch tranh thủ nghỉ ngơi tr&ecirc;n xe.</p>\r\n\r\n<p>Điểm hẹn: Nh&agrave; xe Phương Trang (233 L&ecirc; Hồng Phong, P.4, Q.5) hoặc Bến Xe Miền T&acirc;y (395 Kinh Dương Vương, P. An Lạc, Q. B&igrave;nh T&acirc;n).</p>\r\n\r\n<h3>Ng&agrave;y 2: Bến t&agrave;u Rạch Gi&aacute; - Đảo H&ograve;n Sơn (ăn s&aacute;ng, trưa, tối)</h3>\r\n\r\n<p>S&aacute;ng h&ocirc;m nay, du kh&aacute;ch đ&atilde; c&oacute; mặt ở Bến T&agrave;u Rạch Gi&aacute; &ndash; Ki&ecirc;n Giang v&agrave; khởi h&agrave;nh đi đảo H&ograve;n Sơn. Thời Gian di chuyển mất khoảng 1h45 ph&uacute;t (phụ thuộc v&agrave;o t&igrave;nh h&igrave;nh thời tiết ng&agrave;y h&ocirc;m đ&oacute;).&nbsp;</p>\r\n\r\n<p>T&agrave;u cao tốc h&ocirc;m nay l&agrave; t&agrave;u lớn với sức chứa tr&ecirc;n 300 ghế. Du Kh&aacute;ch c&oacute; thể di chuyển ra boong sau t&agrave;u chụp h&igrave;nh, ngắm v&ugrave;ng biển Vịnh Th&aacute;i Lan khi t&agrave;u rời cảng 30 ph&uacute;t.</p>\r\n\r\n<p>Sau khi đến Cầu Cảng H&ograve;n Sơn, xe sẽ đưa kh&aacute;ch về nhận ph&ograve;ng hoặc tập trung h&agrave;nh l&yacute; tại nh&agrave; nghỉ trước khi đi tham quan một v&ograve;ng đảo. (Th&ocirc;ng thường Nh&agrave; Nghỉ tại đ&acirc;y sẽ cho kh&aacute;ch nhận ph&ograve;ng sau 11h00 trưa).</p>\r\n\r\n<p>Sau khi đi một v&ograve;ng quanh đảo, du kh&aacute;ch sẽ tập trung đi ăn trưa v&agrave;o l&uacute;c 11h30. C&aacute;c m&oacute;n ăn chủ yếu sẽ l&agrave; những m&oacute;n chế biến từ c&aacute; biển, hải sản tươi sống của đảo.&nbsp;</p>\r\n\r\n<p>Buổi chiều, HDV sẽ hướng dẫn đo&agrave;n đi xe m&aacute;y để tham khảo một v&ograve;ng quanh H&ograve;n Sơn (cứ hai kh&aacute;ch đi một xe). Tr&ecirc;n đường đi du kh&aacute;ch sẽ dừng ch&acirc;n tham quan c&aacute;c địa điểm:</p>\r\n\r\n<p>Miếu B&agrave; Cố Chủ (tại B&atilde;i Nh&agrave;). Đ&acirc;y ch&iacute;nh l&agrave; người được xem l&agrave; người đầu ti&ecirc;n c&oacute; c&ocirc;ng khai ph&aacute; H&ograve;n Sơn.</p>\r\n\r\n<p>Miếu Nam Hải Đại Tướng Qu&acirc;n - tức Dinh &Ocirc;ng Nam Hải hay c&ograve;n gọi l&agrave; Đ&igrave;nh thần Nam Hải. Miếu n&agrave;y tọa lạc ngay lối v&agrave;o của b&atilde;i Thi&ecirc;n Tuế. Nếu c&oacute; dịp tham quan nhiều v&ugrave;ng biển, bạn c&oacute; thể nhận ra rằng hầu như v&ugrave;ng biển đảo n&agrave;o cũng c&oacute; nơi thờ thần Nam Hải. Với c&aacute;c ngư d&acirc;n, &ocirc;ng Nam Hải ch&iacute;nh l&agrave; người ph&ugrave; hộ cho những chuyến t&agrave;u đ&aacute;nh bắt được tr&uacute;ng m&ugrave;a t&ocirc;m c&aacute;. &Ocirc;ng Nam Hải cũng l&agrave; vị gi&uacute;p ghe t&agrave;u b&igrave;nh an vượt qua cơn gi&ocirc;ng b&atilde;o để trở về.</p>\r\n\r\n<p>Du kh&aacute;ch được hướng dẫn gh&eacute; thăm những b&atilde;i biển đẹp kh&aacute;c, tranh thủ chụp ảnh&nbsp; với những g&oacute;c h&igrave;nh tuyệt đẹp tại B&atilde;i Thi&ecirc;n Tuế, B&atilde;i đ&aacute; ch&agrave;i, B&atilde;i Xếp, L&agrave;ng Ch&agrave;i Thi&ecirc;n Tuế&hellip; nổi tiếng của đảo</p>\r\n\r\n<p>Đo&agrave;n kh&ocirc;ng qu&ecirc;n dừng ch&acirc;n tắm biển tại B&atilde;i B&agrave;ng &ndash; b&atilde;i biển đẹp nhất H&ograve;n Sơn. Du kh&aacute;ch sẽ c&oacute; thời gian chi&ecirc;m ngưỡng cảnh đẹp tuyệt vời với khung cảnh được tạo n&ecirc;n bởi những dải c&aacute;t trải d&agrave;i, mịn c&ugrave;ng h&agrave;ng dừa nghi&ecirc;ng nghi&ecirc;ng theo gi&oacute;.&nbsp;</p>\r\n\r\n<p>Rời B&atilde;i B&agrave;ng, du kh&aacute;ch tiếp tục h&agrave;nh tr&igrave;nh xuy&ecirc;n đảo v&agrave; dừng ch&acirc;n chụp h&igrave;nh Check in với ch&uacute; R&ugrave;a khổng lồ tại B&atilde;i Bắc</p>\r\n\r\n<p>Một trong những trải nghiệm th&uacute; vị ch&iacute;nh l&agrave; xuy&ecirc;n đảo, chinh phục con đường uốn cong theo sườn n&uacute;i được bao bọc bởi những h&agrave;ng c&acirc;y xanh m&aacute;t rượi. Cung đường sẽ đưa du kh&aacute;ch đến với &ldquo;th&aacute;c nước kh&ocirc;ng t&ecirc;n&rdquo;. (Thời gian tham quan th&aacute;c nước đẹp nhất v&agrave;o th&aacute;ng 9 - th&aacute;ng 11)</p>\r\n\r\n<p>Đo&agrave;n về Nh&agrave; nghỉ để nghỉ ngơi v&agrave; ăn cơm tối, nghỉ đ&ecirc;m tại H&ograve;n Sơn.</p>\r\n\r\n<h3>NG&Agrave;Y 3 | ĐẢO H&Ograve;N SƠN - BẾN T&Agrave;U RẠCH GI&Aacute; &ndash; S&Agrave;I G&Ograve;N (Ăn s&aacute;ng, trưa) &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</h3>\r\n\r\n<p>Ng&agrave;y thứ 3 trong h&agrave;nh tr&igrave;nh, du kh&aacute;ch d&ugrave;ng điểm t&acirc;m v&agrave; c&oacute; một khoảng thời gian tự do tại&nbsp; Chợ đảo H&ograve;n Sơn. Đo&agrave;n tranh thủ mua sắm đặc sản H&ograve;n Sơn về l&agrave;m qu&agrave;, đặc biệt l&agrave; nước mắm nhĩ H&ograve;n Sơn. M&oacute;n n&agrave;y rất nổi tiếng bởi nước mắm nơi đ&acirc;y rất ngon do được l&agrave;m từ c&aacute; cơm biển.</p>\r\n\r\n<p>Sau thời gian mua sắm, du kh&aacute;ch chuẩn bị h&agrave;nh l&yacute;, trả ph&ograve;ng v&agrave; đi d&ugrave;ng cơm trưa. Sau bữa trưa, đo&agrave;n khởi h&agrave;nh về Rạch Gi&aacute;.</p>\r\n\r\n<p>Tại đất liền, nh&acirc;n vi&ecirc;n h&atilde;ng sẽ đưa du kh&aacute;ch về lại điểm đ&oacute;n v&agrave;o ng&agrave;y đầu khởi h&agrave;nh. Đo&agrave;n chia tay nhau, hẹn gặp lại qu&yacute; kh&aacute;ch ở những chuyến đi tiếp theo.&nbsp;</p>\r\n\r\n<p><em>(Thứ tự c&aacute;c địa điểm tham quan trong lịch tr&igrave;nh c&oacute; thể thay đổi nhưng vẫn đảm bảo).</em></p>', 13, 3, '/storage/tours/13/6rCu68vaD4b5BX9wCu6S.jpg', 'tour-du-lich-hon-son-2-ngay-2-dem-4.jpg', '2021-07-19 07:15:36', '2021-07-19 07:15:36', NULL, 'Hòn Sơn là một hòn đảo nhỏ nhưng có sức hút không hề nhỏ: thiên nhiên hoang sơ, những bãi tắm tuyệt đẹp, nước biển trong vắt... Hòn Sơn có vẻ đẹp tuyệt vời giao thoa của rừng núi và biển cả.', '2021-07-30 21:15:00', 2),
(6, 'Tour Hà Giang - cao nguyên đá Đồng Văn 4 ngày 3 đêm', '3830000', '<p><strong>Tour H&agrave; Giang &ndash; cao nguy&ecirc;n đ&aacute; Đồng Văn 4 ng&agrave;y 3 đ&ecirc;m l&agrave; một h&agrave;nh tr&igrave;nh tuyệt vời đến T&acirc;y Bắc. Đ&acirc;y l&agrave; dịp để du kh&aacute;ch được ngắm nh&igrave;n kh&ocirc;ng gian, khung cảnh tuyệt vời của thi&ecirc;n nhi&ecirc;n n&uacute;i đồi v&ugrave;ng cao. Đ&oacute; cũng l&agrave; chuyến đi để t&igrave;m hiểu về n&eacute;t văn h&oacute;a độc đ&aacute;o của đồng b&agrave;o nơi đ&acirc;y. V&agrave; tr&ecirc;n hết, du kh&aacute;ch sẽ được đến với cột cờ Lũng C&uacute;, nơi địa đầu Tổ quốc thi&ecirc;ng li&ecirc;ng.</strong></p>\r\n\r\n<p><img alt=\"Tour Hà Giang - cao nguyên đá Đồng Văn 4 ngày 3 đêm\" src=\"https://trippy.vn/storage/tour/viet-nam/ha-giang/tour-ha-giang-dong-van-4-ngay-3-dem-10.JPG\" /></p>\r\n\r\n<h3>Ng&agrave;y 1: Đ&oacute;n s&acirc;n bay - H&agrave; Nội (ăn: trưa - tối)</h3>\r\n\r\n<p>Ng&agrave;y đầu ti&ecirc;n trong chương tr&igrave;nh, qu&yacute; kh&aacute;ch c&oacute; mặt tại s&acirc;n bay&nbsp;<strong>H&agrave; Nội,</strong>&nbsp;xe v&agrave; HDV đ&oacute;n đo&agrave;n tại s&acirc;n bay Nội B&agrave;i. Đo&agrave;n di chuyển về H&agrave; Nội ăn trưa v&agrave; nhận ph&ograve;ng kh&aacute;ch sạn.</p>\r\n\r\n<p><em><strong>Buổi chiều,&nbsp;</strong></em>đo&agrave;n sẽ khởi h&agrave;nh đi tham quan v&ograve;ng quanh th&agrave;nh phố với c&aacute;c điểm tham quan nổi bật. C&aacute;c địa điểm lần lượt sẽ l&agrave; Hồ T&acirc;y, ch&ugrave;a Trấn Quốc, Hồ Gươm (biểu tượng của H&agrave; Nội), đền Ngọc Sơn, cầu Th&ecirc; H&uacute;c. Đ&acirc;y đều l&agrave; những địa danh nổi tiếng của thủ đ&ocirc; ng&agrave;n năm văn hiến. Một chuyến du lịch đến H&agrave; Nội sẽ thật thiếu s&oacute;t nếu kh&ocirc;ng gh&eacute; qua c&aacute;c địa điểm n&agrave;y.&nbsp;</p>\r\n\r\n<p>Đo&agrave;n cũng dừng ch&acirc;n chụp h&igrave;nh kỷ niệm tại&nbsp;<strong>Nh&agrave; Thờ Lớn, Nh&agrave; H&aacute;t Lớn th&agrave;nh phố</strong>. Đ&acirc;y l&agrave; c&aacute;c biểu tượng mang đầy dấu ấn lịch sử thời đại. B&ecirc;n cạnh tham quan, qu&yacute; kh&aacute;ch sẽ c&oacute; dịp thưởng thức đặc sản H&agrave; Nội. Đ&oacute; l&agrave; b&aacute;nh cốm H&agrave;ng Than, kem Tr&agrave;ng Tiền.</p>\r\n\r\n<p><em><strong>V&agrave;o buổi tối,</strong></em>&nbsp;đo&agrave;n đi xem&nbsp;<strong>show M&uacute;a Rối Nước</strong>. Đ&acirc;y l&agrave; Di Sản Văn H&oacute;a Phi Vật Thể đặc sắc của d&acirc;n tộc. Sau bữa tối, đo&agrave;n về kh&aacute;ch sạn nghỉ ngơi hoặc tự do kh&aacute;m ph&aacute; H&agrave; Nội.</p>\r\n\r\n<h3>Ng&agrave;y 2: H&agrave; Nội - H&agrave; Giang - Quản Bạ - Y&ecirc;n Minh (ăn S&aacute;ng/Trưa/Tối)</h3>\r\n\r\n<p>Ng&agrave;y thứ 2 của h&agrave;nh tr&igrave;nh, xe sẽ đ&oacute;n đo&agrave;n khởi h&agrave;nh đi H&agrave; Giang. Tr&ecirc;n đường đi, đo&agrave;n sẽ nghỉ ngơi, ăn s&aacute;ng tại ng&atilde; 3 Kim Anh. (Đo&agrave;n d&ugrave;ng khẩu phần ăn s&aacute;ng được chuẩn bị bởi kh&aacute;ch sạn).</p>\r\n\r\n<p><em><strong>Buổi trưa,</strong></em>&nbsp;qu&yacute; kh&aacute;ch ăn trưa tại thị trấn T&acirc;n Y&ecirc;n (H&agrave;m Y&ecirc;n, Tuy&ecirc;n Quang).</p>\r\n\r\n<p>Tiếp tục h&agrave;nh tr&igrave;nh, đo&agrave;n dừng ch&acirc;n gh&eacute; thăm&nbsp;<strong>Đền Đ&ocirc;i C&ocirc; - Cầu M&aacute; linh thi&ecirc;ng</strong>&nbsp;b&ecirc;n bờ S&ocirc;ng L&ocirc;. Đền được x&acirc;y dựng v&agrave;o năm 1929. Nơi đ&acirc;y thờ phụng v&agrave; ngưỡng vọng c&ocirc; đ&ocirc;i Thượng Ng&agrave;n v&agrave; c&ocirc; bơ Thoải Cung (con của Mẫu Thượng Ng&agrave;n v&agrave; Mẫu Thoải Cung).</p>\r\n\r\n<p>Đến TP. H&agrave; Giang, đo&agrave;n dừng ch&acirc;n chụp h&igrave;nh kỷ niệm tại&nbsp;<strong>Km0</strong>&nbsp;của H&agrave; Giang. Điểm tiếp theo l&agrave;&nbsp;<strong>Cổng Trời Quản Bạ.</strong>&nbsp;Tại đ&acirc;y qu&yacute; kh&aacute;ch chụp h&igrave;nh kỷ niệm v&agrave; chi&ecirc;m ngưỡng&nbsp;<strong>N&uacute;i đ&ocirc;i C&ocirc; Ti&ecirc;n (N&uacute;i đ&ocirc;i Quản Bạ)&nbsp;</strong>v&agrave;&nbsp;to&agrave;n cảnh thị trấn Tam Sơn từ tr&ecirc;n cao.</p>\r\n\r\n<p>Xe đi đến&nbsp;<strong>Y&ecirc;n Minh,</strong>&nbsp;đo&agrave;n dừng ch&acirc;n, nhận ph&ograve;ng kh&aacute;ch sạn. Nghỉ đ&ecirc;m tại Y&ecirc;n Minh.</p>\r\n\r\n<h3>Ng&agrave;y 3: Y&ecirc;n Minh - Lũng C&uacute; - M&atilde; P&iacute; L&egrave;ng - Đồng Văn (s&aacute;ng, trưa, tối)</h3>\r\n\r\n<p>Tour H&agrave; Giang &ndash; cao nguy&ecirc;n đ&aacute; Đồng Văn 4 ng&agrave;y 3 đ&ecirc;m đ&atilde; đi được đến ng&agrave;y thứ 3. V&agrave;o ng&agrave;y n&agrave;y, sau khi d&ugrave;ng điểm t&acirc;m, qu&yacute; kh&aacute;ch sẽ được l&ecirc;n đường chi&ecirc;m ngưỡng cảnh đẹp của C&ocirc;ng vi&ecirc;n địa chất to&agrave;n cầu Cao nguy&ecirc;n đ&aacute; Đồng Văn.&nbsp;</p>\r\n\r\n<p>Tr&ecirc;n đường đi, đo&agrave;n sẽ gh&eacute; v&agrave;o thăm&nbsp;<strong>Phố C&aacute;o</strong>. Đ&acirc;y l&agrave; nơi c&oacute; những ng&ocirc;i nh&agrave; đặc trưng của người H&#39;m&ocirc;ng bởi những h&agrave;ng r&agrave;o được x&acirc;y bằng c&aacute;ch xếp đ&aacute; l&ecirc;n nhau.</p>\r\n\r\n<p>Tiếp đ&oacute;, du kh&aacute;ch sẽ đến với bản&nbsp;<strong>Sủng L&agrave;.&nbsp;</strong>Đ&acirc;y l&agrave; nơi c&oacute; ng&ocirc;i nh&agrave; Cổ của người H&#39;m&ocirc;ng với tường tr&igrave;nh bằng đất. Được biết đ&oacute; cũng l&agrave; nơi được sử dụng l&agrave;m bối cảnh trong bộ phim nổi tiếng &quot;Chuyện của Pao&quot;.</p>\r\n\r\n<p>Bộ phim l&agrave; t&aacute;c phẩm của đạo diễn Ng&ocirc; Quang Hải, được chuyển thể từ truyện ngắn &quot;Tiếng đ&agrave;n m&ocirc;i sau bờ r&agrave;o đ&aacute;&quot; của nh&agrave; văn Đỗ B&iacute;ch Thủy. Phim sản xuất năm 2006 v&agrave; gi&agrave;nh được 4 giải C&aacute;nh diều v&agrave;ng.&nbsp;</p>\r\n\r\n<p>Sau khi gh&eacute; nh&agrave; tường tr&igrave;nh, đo&agrave;n dừng ch&acirc;n nghỉ ngơi, chụp h&igrave;nh với hoa tam gi&aacute;c mạch gần dốc ch&iacute;n khoanh.</p>\r\n\r\n<p>Tiếp đ&oacute;, du kh&aacute;ch đến với&nbsp;<strong>Dinh Vua M&egrave;o - Vương Ch&iacute;nh Đức</strong>&nbsp;nằm trong một thung lũng của x&atilde; S&agrave; Ph&igrave;n. C&ocirc;ng tr&igrave;nh n&agrave;y x&acirc;y dựng trong 9 năm, tốn tương đương khoảng 150 tỷ đồng. Vương Ch&iacute;nh Đức cũng ch&iacute;nh l&agrave; người duy nhất được đồng b&agrave;o người M&ocirc;ng nơi đ&acirc;y suy t&ocirc;n l&agrave; vua M&egrave;o.</p>\r\n\r\n<p>Từ Dinh Thự Vua M&egrave;o, tr&ecirc;n đường đi M&egrave;o Vạc du kh&aacute;ch sẽ chinh phục&nbsp;<strong>đ&egrave;o M&atilde; P&iacute; L&egrave;ng.</strong>&nbsp;Đ&acirc;y cũng l&agrave; đoạn đẹp nhất tr&ecirc;n con đường mang t&ecirc;n &quot;Đường Hạnh ph&uacute;c&quot;.</p>\r\n\r\n<p>Du kh&aacute;ch chụp h&igrave;nh với quang cảnh tuyệt đẹp của hẻm vực M&atilde; P&iacute; L&egrave;ng s&acirc;u 800m. Điều đặc biệt l&agrave; đ&acirc;y l&agrave; nơi địa h&igrave;nh bị chia cắt s&acirc;u nhất của Việt Nam.&nbsp;</p>\r\n\r\n<p>H&agrave;nh tr&igrave;nh tham quan lại tiếp tục khi xe đưa du kh&aacute;ch đi qua thị trấn M&egrave;o Vạc để về Y&ecirc;n Minh. Đo&agrave;n sẽ quay lại thị trấn Đồng Văn ăn trưa, nhận ph&ograve;ng kh&aacute;ch sạn, nghỉ ngơi.</p>\r\n\r\n<p><em><strong>Buổi chiều,</strong></em>&nbsp;du kh&aacute;ch sẽ đi thăm&nbsp;<strong>Cột Cờ Lũng C&uacute;</strong>&nbsp;- nơi địa đầu linh thi&ecirc;ng của Tổ quốc. Đ&acirc;y ch&iacute;nh l&agrave; điểm c&oacute; vĩ độ cao nhất tr&ecirc;n bản đồ của Việt Nam.</p>\r\n\r\n<p>Sau khi tham quan, đo&agrave;n về lại thị trấn<strong>&nbsp;Đồng Văn</strong>&nbsp;nghỉ ngơi.</p>\r\n\r\n<p>Sau bữa tối, qu&yacute; kh&aacute;ch tự do kh&aacute;m ph&aacute;&nbsp;<strong>Phố Cổ Đồng Văn.&nbsp;</strong>Tận hưởng thời gian nh&acirc;m nhi thưởng thức một ly c&agrave; ph&ecirc; tại qu&aacute;n C&agrave; ph&ecirc; phố Cổ (ph&iacute; tự t&uacute;c).</p>\r\n\r\n<h3>Ng&agrave;y 4: Đồng Văn - H&agrave; Giang - H&agrave; Nội - Tiễn s&acirc;n bay (ăn s&aacute;ng - trưa - tối)</h3>\r\n\r\n<p>Ng&agrave;y cuối c&ugrave;ng trong chương tr&igrave;nh tour H&agrave; Giang &ndash; cao nguy&ecirc;n đ&aacute; Đồng Văn 4 ng&agrave;y 3 đ&ecirc;m, du kh&aacute;ch sẽ dậy thật sớm để tham gia chợ phi&ecirc;n. Đ&acirc;y l&agrave; dịp để qu&yacute; kh&aacute;ch chứng kiến cảnh b&agrave; con đồng b&agrave;o d&acirc;n tộc n&ocirc; nức về với chợ phi&ecirc;n&nbsp;Đồng Văn để tham gia chợ phi&ecirc;n, thường được họp s&aacute;ng chủ nhật h&agrave;ng tuần.</p>\r\n\r\n<p>Sau khi tham gia chợ phi&ecirc;n, đo&agrave;n ăn s&aacute;ng v&agrave; l&ecirc;n xe về H&agrave; Nội. Tr&ecirc;n đường về, buổi trưa đo&agrave;n gh&eacute; th&agrave;nh phố H&agrave; Giang.&nbsp;</p>\r\n\r\n<p>Về đến H&agrave; Nội, Xe đưa đo&agrave;n ra thẳng s&acirc;n bay. Đo&agrave;n chia tay nhau, du kh&aacute;ch l&agrave;m thủ tục đ&aacute;p chuyến bay về lại S&agrave;i G&ograve;n. (Qu&yacute; kh&aacute;ch vui l&ograve;ng book chuyến bay từ 21h30 để kịp giờ xe di chuyển về H&agrave; Nội).&nbsp;</p>\r\n\r\n<p>Hẹn gặp lại qu&yacute; kh&aacute;ch trong những chuyến đi gần nhất.</p>', 13, 2, '/storage/tours/13/nbVMFwGajGuOLUDA3uHr.jpg', 'tour-ha-giang-dong-van-4-ngay-3-dem-15.jpg', '2021-07-19 07:20:46', '2021-07-19 07:20:46', NULL, 'Với phong cảnh nên thơ ngoạn mục và nền văn hóa độc đáo của đồng bào Mông, Hà Giang và Cao Nguyên Đá Đồng Văn là một trong những địa điểm du lịch phía Bắc rất được du khách yêu thích.', '2021-07-23 21:50:00', 4);
INSERT INTO `tours` (`id`, `name`, `price`, `content`, `user_id`, `category_tour_id`, `feature_image_path`, `feature_image_name`, `created_at`, `updated_at`, `deleted_at`, `description`, `departed`, `duration`) VALUES
(7, 'ĐỘNG THIÊN ĐƯỜNG – ĐỘNG PHONG NHA', '1000000', '<p>Chương tr&igrave;nh&nbsp;<strong>tour&nbsp;<a href=\"https://phongnhaexplorer.com/cam-nang-du-lich-mien-trung/du-lich-quang-binh.html\" target=\"_top\" title=\"du lịch Quảng Bình\">du lịch Quảng B&igrave;nh</a>&nbsp;1 ng&agrave;y</strong>&nbsp;kh&aacute;m ph&aacute; hai hang động nổi tiếng của&nbsp;<a href=\"https://phongnhaexplorer.com/phong-nha/vuon-quoc-gia-phong-nha-ke-bang.html\" target=\"_top\" title=\"Vườn Quốc Gia Phong Nha Kẻ Bàng\">Vườn Quốc Gia Phong Nha Kẻ B&agrave;ng</a>&nbsp;l&agrave;&nbsp;<a href=\"https://phongnhaexplorer.com/phong-nha/dong-phong-nha-4.html\" target=\"_self\" title=\"Động Phong Nha\">Động Phong Nha</a>&nbsp;v&agrave;&nbsp;<a href=\"https://phongnhaexplorer.com/phong-nha/dong-thien-duong.html\" target=\"_top\" title=\"Động Thiên Đường\">Động Thi&ecirc;n Đường</a>&nbsp;gi&uacute;p qu&yacute; kh&aacute;ch c&oacute; những trải nghiệm đ&aacute;ng nhớ về v&ugrave;ng đất gi&oacute; L&agrave;o, c&aacute;t trắng.&nbsp;<strong><a href=\"https://phongnhaexplorer.com/\" target=\"_self\" title=\"Tour Phong Nha\">Tour Phong Nha</a>&nbsp;Kẻ B&agrave;ng 1 ng&agrave;y&nbsp;</strong>tham quan<strong>&nbsp;</strong><a href=\"https://phongnhaexplorer.com/phong-nha/dong-thien-duong.html\" target=\"_top\" title=\"Động Thiên Đường\">Động Thi&ecirc;n Đường</a>&nbsp;&ndash;&nbsp;<a href=\"https://phongnhaexplorer.com/phong-nha/dong-phong-nha-4.html\" target=\"_self\" title=\"Động Phong Nha\">Động Phong Nha</a>&nbsp;gh&eacute;p đo&agrave;n khởi h&agrave;nh h&agrave;ng ng&agrave;y gi&aacute; rẻ m&agrave; vẫn được bảo đảm chất lượng dịch vụ tốt nhất.&nbsp;Ngo&agrave;i ra, ch&uacute;ng t&ocirc;i c&ograve;n tổ chức&nbsp;<strong>Tour Động Phong Nha &ndash;&nbsp;<a href=\"https://phongnhaexplorer.com/phong-nha/dong-thien-duong.html\" target=\"_top\" title=\"Động Thiên Đường\">Động Thi&ecirc;n Đường</a>&nbsp; 1 ng&agrave;y&nbsp;</strong>ri&ecirc;ng theo y&ecirc;u cầu của qu&yacute; kh&aacute;ch.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\">\r\n			<h3>CHƯƠNG TR&Igrave;NH GH&Eacute;P ĐO&Agrave;N KHỞI H&Agrave;NH H&Agrave;NG NG&Agrave;Y</h3>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>S&aacute;ng</td>\r\n			<td>\r\n			<p>Xe v&agrave; HDV đ&oacute;n Qu&yacute; kh&aacute;ch tại c&aacute;c Kh&aacute;ch sạn ở th&agrave;nh phố&nbsp;Đồng Hới<strong>,&nbsp;</strong>c&aacute;c kh&aacute;ch sạn ở khu vực Phong Nha, nh&agrave; ga, s&acirc;n bay, bến xe Đồng Hới (Đặc biệt c&oacute; xe đ&oacute;n từ c&aacute;c chuyến t&agrave;u SE8 04:45AM, SE3 05:21AM, SE19 05:59AM v&agrave; c&aacute;c xe bus đến Đồng Hới s&aacute;ng sớm về c&ocirc;ng ty&nbsp;<strong>nhận ph&ograve;ng nghỉ ngơi miễn ph&iacute;</strong>&nbsp;chờ khởi h&agrave;nh)</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>08:00</td>\r\n			<td>\r\n			<p>Khởi h&agrave;nh đi&nbsp;<strong>Vườn quốc gia Phong Nha - Kẻ B&agrave;ng</strong>&nbsp;tham quan&nbsp;<strong>Động Thi&ecirc;n Đường</strong>&nbsp;<em>&quot;Ho&agrave;ng cung trong l&ograve;ng đất&quot;</em></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>09:00</td>\r\n			<td>\r\n			<p>H&agrave;nh tr&igrave;nh từ km 16 của đường Hồ Ch&iacute; Minh nh&aacute;nh T&acirc;y c&aacute;ch trung t&acirc;m th&agrave;nh phố Đồng Hới chừng 65km về hướng T&acirc;y Bắc, băng qua những t&aacute;n rừng gi&agrave; ngợp l&aacute;, những d&atilde;y n&uacute;i đ&aacute; v&ocirc;i tr&ugrave;ng tr&ugrave;ng điệp điệp, du kh&aacute;ch sẽ đến&nbsp;<strong>Động Thi&ecirc;n Đường</strong>, hang động kh&ocirc; d&agrave;i nhất ch&acirc;u &Aacute; đ&atilde; được c&aacute;c nh&agrave; th&aacute;m hiểm Ho&agrave;ng gia Anh ph&aacute;t hiện v&agrave;o năm 2005 l&agrave; một&nbsp; trong những kỳ quan tr&aacute;ng lệ v&agrave; huyền ảo bậc nhất&nbsp; thế giới với vẻ đẹp lộng lẫy của m&igrave;nh n&ecirc;n được mệnh danh&nbsp;<strong>&quot;Ho&agrave;ng cung trong l&ograve;ng đất&quot;</strong>.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>09:20</td>\r\n			<td>Qu&yacute; kh&aacute;ch l&ecirc;n xe điện trung chuyển từ b&atilde;i đậu xe v&agrave;o tận ch&acirc;n n&uacute;i của hang động khoảng 2km. Hai b&ecirc;n đường l&agrave; c&aacute;nh rừng nguy&ecirc;n sinh c&acirc;y cối xanh tươi, m&aacute;t rượi, tiếng chim h&oacute;t, tiếng ve k&ecirc;u nghe thật vui tai.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>10:00</td>\r\n			<td>Qu&yacute; kh&aacute;ch đi bộ men theo sườn n&uacute;i đến cửa hang Động Thi&ecirc;n Đường, cửa v&agrave;o động nhỏ hẹp nhưng bước v&agrave;o b&ecirc;n trong Qu&yacute; kh&aacute;ch sẽ kh&ocirc;ng khỏi ngỡ ng&agrave;ng bởi trần hang cao v&agrave; rộng, chi&ecirc;m ngưỡng v&ocirc; v&agrave;n thạch nhũ với nhiều h&igrave;nh th&ugrave; kh&aacute;c nhau như&nbsp;<strong>Thạch Thi&ecirc;n Cung, Th&aacute;c Thi&ecirc;n H&agrave;, Quần Ti&ecirc;n hội tụ, Th&aacute;p Li&ecirc;n hoa</strong>&hellip; Trước mắt Qu&yacute; kh&aacute;ch l&agrave; h&igrave;nh ảnh của ruộng bậc thang đặc trưng của nền văn minh l&uacute;a nước,&nbsp; cột thạch nhũ h&igrave;nh th&aacute;p Chămpa tuyệt đẹp, hay tượng Phật B&agrave; Quan &Acirc;m, ba vị Ph&uacute;c Lộc Thọ quen thuộc, cho đến biểu tượng nh&agrave; r&ocirc;ng của đồng b&agrave;o T&acirc;y nguy&ecirc;n xa x&ocirc;i...</td>\r\n		</tr>\r\n		<tr>\r\n			<td>11:30</td>\r\n			<td>&nbsp;Qu&yacute; kh&aacute;ch đến nh&agrave; h&agrave;ng nghỉ ngơi v&agrave; d&ugrave;ng bữa trưa với những m&oacute;n ăn đặc sản địa phương (xem&nbsp;<strong><a href=\"https://phongnhaexplorer.com/thuc-don-tour-vuon-quoc-gia-phong-nha-ke-bang/\" rel=\"noopener\" target=\"_blank\">Thực đơn</a>)</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>13:30</td>\r\n			<td>Bắt đầu h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute;&nbsp;<strong>Động Phong Nha</strong>&nbsp;&quot;Đệ Nhất kỳ quan&quot;.\r\n			<p>Qu&yacute; kh&aacute;ch l&ecirc;n thuyền ngược d&ograve;ng Son thơ mộng, thưởng ngoạn phong cảnh thi&ecirc;n nhi&ecirc;n hữu t&igrave;nh hai b&ecirc;n bờ s&ocirc;ng, du kh&aacute;ch sẽ bắt gặp h&igrave;nh ảnh những thiếu nữ, những đứa trẻ vui đ&ugrave;a b&ecirc;n bờ s&ocirc;ng c&oacute; dịp trải nghiệm cuộc sống sinh hoạt của x&oacute;m l&agrave;ng ven s&ocirc;ng mang đậm hồn qu&ecirc; Việt.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>14:00</td>\r\n			<td>Chinh phục&nbsp;<strong>Động Phong Nha</strong>: Hang nước d&agrave;i nhất Đ&ocirc;ng Nam &Aacute; tận hưởng cảnh quan n&uacute;i đ&aacute; v&ocirc;i ngoạn mục, hệ thống s&ocirc;ng ngầm v&agrave; thạch nhủ c&oacute; một kh&ocirc;ng hai ở tr&ecirc;n thế giới. Kh&aacute;m ph&aacute; hang C&ocirc; Ti&ecirc;n &amp; Cung Đ&igrave;nh chi&ecirc;m ngưỡng c&aacute;c khối thạch nhũ tuyệt đẹp được kiến tạo bởi thi&ecirc;n nhi&ecirc;n qua h&agrave;ng ng&agrave;n thi&ecirc;n ni&ecirc;n kỷ.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>16:30</td>\r\n			<td>Tạm biệt&nbsp;<strong>Vườn quốc gia Phong Nha - Kẻ B&agrave;ng</strong>, Qu&yacute; kh&aacute;ch l&ecirc;n xe quay trở về&nbsp;<strong>Th&agrave;nh phố Đồng Hới</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td>17:30</td>\r\n			<td>Xe đưa qu&yacute; kh&aacute;ch về kh&aacute;ch sạn, nh&agrave; ga, bến xe, s&acirc;n bay Đồng Hới. (Nếu chuyến đi muộn th&igrave; về&nbsp;<strong>nhận ph&ograve;ng nghỉ ngơi miễn ph&iacute;</strong>&nbsp;tại c&ocirc;ng ty. Đến giờ tiễn qu&yacute; kh&aacute;ch ra&nbsp;nh&agrave; ga, bến xe, s&acirc;n bay Đồng Hới )\r\n			<p><em><strong>Phong Nha Explorer chia tay đo&agrave;n, kết th&uacute;c chương tr&igrave;nh hẹn gặp lại !</strong></em></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 1, 6, '/storage/tours/1/XYXMO8sVj0hb1UY9sAJY.jpg', 'dongphongnha112.jpg', '2021-07-23 01:32:15', '2021-07-23 01:32:15', NULL, 'Quảng Bình như một bức tranh hoành tráng với nhiều cảnh quan thiên nhiên hùng vĩ: đèo Ngang, biển Nhật Lệ, Đá Nhảy đặc biệt là Vườn quốc gia Phong Nha – Kẻ Bàng di sản thiên nhiên thế giới.', '2021-07-25 15:30:00', 1),
(8, 'TOUR DU LỊCH ĐẢO CỒN CỎ QUẢNG TRỊ 2 NGÀY 1 ĐÊM', '1350000', '<table>\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\">NG&Agrave;Y 1: QUẢNG TRỊ - ĐẢO CỒN CỎ (Ăn trưa, tối)</td>\r\n		</tr>\r\n		<tr>\r\n			<td>S&aacute;ng</td>\r\n			<td>\r\n			<p><strong>05h00:</strong>&nbsp;Đ&oacute;n kh&aacute;ch tại nh&agrave; ga, bến xe, văn ph&ograve;ng xe bus, c&aacute;c kh&aacute;ch sạn tại th&agrave;nh phố Đồng Hới khởi h&agrave;nh đi Quảng Trị.</p>\r\n\r\n			<p><strong>06h30</strong>: Đ&oacute;n kh&aacute;ch tại khu vực TP Đ&ocirc;ng H&agrave;&nbsp;khởi h&agrave;nh đến Cảng Cửa Việt.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>07:30</td>\r\n			<td>\r\n			<p>Qu&yacute; kh&aacute;ch l&ecirc;n t&agrave;u cao tốc đi Đảo Cồn Cỏ. Tự do ngắm cảnh biển khơi.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>9:00</td>\r\n			<td>\r\n			<p>Đến cảng Đảo Cồn Cỏ, bắt đầu chương tr&igrave;nh kh&aacute;m ph&aacute; Đảo Cồn Cỏ&nbsp;<strong>bằng xe điện</strong>&nbsp;với c&aacute;c điểm tham quan v&ocirc; c&ugrave;ng hấp dẫn.</p>\r\n\r\n			<p>Đo&agrave;n tập trung chụp ảnh lưu niệm tại&nbsp;<strong>Cổng ch&agrave;o huyện đảo Cồn Cỏ</strong>.&nbsp;</p>\r\n\r\n			<p><strong>Cột Cờ Tổ Quốc:&nbsp;</strong>l&agrave; một trong những cột cờ được xếp v&agrave;o loại lớn tr&ecirc;n c&aacute;c đảo ven biển Việt Nam cao 38,8m với l&aacute; Quốc kỳ rộng 24m&shy;2. Cột cờ tổ quốc huyện đảo Cồn Cỏ thể hiện sự kỳ vọng của bao thế hệ; l&agrave; biểu tượng của độc lập, h&ograve;a b&igrave;nh; l&agrave; sự khẳng định chủ quyền thi&ecirc;ng li&ecirc;ng của tổ quốc.</p>\r\n\r\n			<p>Đến viếng&nbsp;<strong>Đ&agrave;i tưởng niệm liệt sĩ Đảo Cồn Cỏ</strong>&nbsp;tưởng nhớ c&ocirc;ng lao của c&aacute;c chiến sĩ đ&atilde; hy sinh để bảo vệ chủ quyền biển đảo Tổ Quốc.</p>\r\n\r\n			<p>Về nh&agrave; kh&aacute;ch, nhận ph&ograve;ng nghỉ ngơi.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Trưa</td>\r\n			<td>\r\n			<p>Qu&yacute; kh&aacute;ch nghỉ ngơi d&ugrave;ng bữa trưa với c&aacute;c m&oacute;n ăn đặc sản biển đảo.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Chiều</td>\r\n			<td>\r\n			<p>Tiếp tục h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute;&nbsp;<strong>khu rừng nhiệt đới</strong>&nbsp;rậm rạp vẫn c&ograve;n nguy&ecirc;n trạng l&agrave; nơi tr&uacute; ngụ của nhiều sinh vật tr&ecirc;n đảo.</p>\r\n\r\n			<p>Chinh phục đỉnh&nbsp;<strong>hải đăng Cồn Cỏ&nbsp;</strong>l&agrave; điểm cao nhất của đảo, bạn c&oacute; thể ph&oacute;ng tầm mắt nh&igrave;n to&agrave;n cảnh đảo Cồn Cỏ,&nbsp;tận hưởng kh&ocirc;ng kh&iacute; trong l&agrave;nh,gi&oacute; biển m&aacute;t rượi v&agrave; chi&ecirc;m ngưỡng phong cảnh thanh b&igrave;nh nơi đ&acirc;y.</p>\r\n\r\n			<p>Tham quan&nbsp;<strong>đ&agrave;i quan s&aacute;t Th&aacute;i Văn A&nbsp;</strong>nơi anh h&ugrave;ng Th&aacute;i Văn A quan s&aacute;t để x&aacute;c định vị tr&iacute; m&aacute;y bay hay t&agrave;u chiến đối phương, rồi th&ocirc;ng b&aacute;o cho c&aacute;c đơn vị ph&aacute;o cao xạ bắn v&agrave;o mục ti&ecirc;u.</p>\r\n\r\n			<p>Đo&agrave;n đi thăm&nbsp;<strong>Bến Ngh&egrave;</strong>&nbsp;&ndash; điểm đ&oacute;n tia nắng đầu ti&ecirc;n tr&ecirc;n đảo với&nbsp;những b&atilde;i đ&aacute; bazan độc đ&aacute;o. Bến Ngh&egrave; ch&iacute;nh l&agrave; nơi cập bến của những người đ&ecirc;m đ&ecirc;m vượt biển tiếp tế cho đảo trong chiến tranh.</p>\r\n\r\n			<p>Về&nbsp;<strong>bến Tranh&nbsp;</strong>tự do tắm biển, lặn ngắm san h&ocirc;.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>19:00</td>\r\n			<td>Qu&yacute; kh&aacute;ch d&ugrave;ng bữa tối với những hải sản tươi ngon, nghỉ đ&ecirc;m tại đảo.</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\">NG&Agrave;Y 2: QUẢNG TRỊ - TIỄN KH&Aacute;CH (Ăn s&aacute;ng)</td>\r\n		</tr>\r\n		<tr>\r\n			<td>S&aacute;ng</td>\r\n			<td>\r\n			<p>Qu&yacute; kh&aacute;ch thức dậy ngắm b&igrave;nh minh. Ăn s&aacute;ng l&agrave;m thủ tục trả ph&ograve;ng.</p>\r\n\r\n			<p>07h00: Xe điện đưa qu&yacute; kh&aacute;ch về cảng Cồn Cỏ.</p>\r\n\r\n			<p>Q&uacute;y kh&aacute;ch l&ecirc;n t&agrave;u tạm biệt đảo Cồn Cỏ về lại đất liền.&nbsp;</p>\r\n\r\n			<p>Đến bến Cửa Việt trả kh&aacute;ch về TP Đ&ocirc;ng H&agrave; hoặc Đồng Hới.</p>\r\n\r\n			<p><strong><em>Thay mặt Phong Nha Explorer ch&agrave;o tạm biệt qu&yacute; kh&aacute;ch, hẹn gặp lại !</em></strong></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 1, 6, '/storage/tours/1/SnRIQL3kN2V2vQAdI1HF.jpg', 'tour-du-lich-con-co-1.jpg', '2021-07-23 01:37:01', '2021-07-23 01:37:01', NULL, 'Du lịch Cồn Cỏ để hòa mình vào không gian biển khơi trên một hòn đảo nhỏ hoang sơ nơi có những bãi đá Bazan hình thành từ núi lửa, những bãi tắm cát trắng ẩn hiện vỏ ốc.', '2021-07-24 15:36:00', 2),
(9, 'TOUR DU LỊCH QUẢNG BÌNH – CHÙA HƯƠNG HÀ TĨNH 2 NGÀY 1 ĐÊM', '2690000', '<table>\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"2\">NG&Agrave;Y 1: ĐỘNG THI&Ecirc;N ĐƯỜNG - ĐỘNG PHONG NHA (Ăn trưa)</td>\r\n		</tr>\r\n		<tr>\r\n			<td>S&Aacute;NG</td>\r\n			<td>\r\n			<p>Xe v&agrave; HDV đ&oacute;n Qu&yacute; kh&aacute;ch tại<strong>&nbsp;Đồng Hới</strong>&nbsp;về kh&aacute;ch sạn gửi h&agrave;nh l&yacute; sau đ&oacute; khởi h&agrave;nh đi<strong>&nbsp;Động Thi&ecirc;n Đường</strong>.</p>\r\n\r\n			<p>H&agrave;nh tr&igrave;nh từ km 16 của đường<strong>&nbsp;Hồ Ch&iacute; Minh nh&aacute;nh T&acirc;y</strong>, băng qua những t&aacute;n rừng gi&agrave; ngợp l&aacute;, du kh&aacute;ch sẽ đến&nbsp;<strong>Động Thi&ecirc;n Đường</strong>, hang động kh&ocirc; d&agrave;i nhất ch&acirc;u &Aacute; đ&atilde; được c&aacute;c nh&agrave; th&aacute;m hiểm Ho&agrave;ng gia Anh ph&aacute;t hiện v&agrave;o năm 2005 l&agrave; một trong những kỳ quan tr&aacute;ng lệ v&agrave; huyền ảo bậc nhất thế giới với vẻ đẹp lộng lẫy của m&igrave;nh n&ecirc;n được mệnh danh&nbsp;<strong>&quot;Ho&agrave;ng cung trong l&ograve;ng đất&quot;</strong>.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>TRƯA</td>\r\n			<td>Qu&yacute; kh&aacute;ch nghỉ ngơi v&agrave; ăn trưa tại nh&agrave; h&agrave;ng với những m&oacute;n ăn đặc sản địa phương.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>CHIỀU</td>\r\n			<td>\r\n			<p>Du thuyền ngược d&ograve;ng<strong>&nbsp;s&ocirc;ng Son</strong>&nbsp;thơ mộng, thưởng ngoạn phong cảnh thi&ecirc;n nhi&ecirc;n hữu t&igrave;nh hai b&ecirc;n bờ s&ocirc;ng Son, du kh&aacute;ch sẽ bắt gặp h&igrave;nh ảnh những thiếu nữ, những đứa trẻ vui đ&ugrave;a b&ecirc;n bờ s&ocirc;ng trải nghiệm cuộc sống sinh hoạt của x&oacute;m l&agrave;ng ven s&ocirc;ng mang đậm hồn qu&ecirc; Việt.</p>\r\n\r\n			<p>Chinh phục&nbsp;<strong>Động Phong Nha</strong>&nbsp;với hệ thống s&ocirc;ng ngầm v&agrave; thạch nhủ c&oacute; một kh&ocirc;ng hai ở tr&ecirc;n thế giới. Kh&aacute;m ph&aacute; hang C&ocirc; Ti&ecirc;n &amp; Cung Đ&igrave;nh chi&ecirc;m ngưỡng c&aacute;c khối thạch nhũ tuyệt đẹp được kiến tạo bởi thi&ecirc;n nhi&ecirc;n qua h&agrave;ng ng&agrave;n thi&ecirc;n ni&ecirc;n kỷ.</p>\r\n\r\n			<p>Qu&yacute; kh&aacute;ch về kh&aacute;ch sạn&nbsp;nhận ph&ograve;ng&nbsp;nghỉ ngơi, tự do tắm biển&nbsp;<strong>Nhật Lệ</strong>&nbsp;nằm ngay cạnh cửa&nbsp;<strong>s&ocirc;ng Nhật Lệ</strong>&nbsp;được thi&ecirc;n nhi&ecirc;n ban tặng b&atilde;i c&aacute;t c&aacute;t trắng v&agrave; nước biển trong xanh c&ograve;n nguy&ecirc;n vẻ hoang sơ.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>TỐI</td>\r\n			<td>Qu&yacute; kh&aacute;ch&nbsp;<strong>tự do ăn tối</strong>, nghỉ ngơi trong kh&aacute;ch sạn hay kh&aacute;m ph&aacute; th&agrave;nh phố Đồng Hới về đ&ecirc;m.</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\">NG&Agrave;Y 2:&nbsp;H&Agrave; TĨNH - C&Aacute;P TREO - CH&Ugrave;A HƯƠNG T&Iacute;CH (Ăn s&aacute;ng, trưa)</td>\r\n		</tr>\r\n		<tr>\r\n			<td>S&Aacute;NG</td>\r\n			<td>\r\n			<p>Qu&yacute; kh&aacute;ch d&ugrave;ng bữa s&aacute;ng, l&agrave;m thủ tục&nbsp;trả ph&ograve;ng,&nbsp;khởi h&agrave;nh đi H&agrave; Tĩnh.</p>\r\n\r\n			<p>Dừng ch&acirc;n ngắm&nbsp;<strong>Đ&egrave;o Ngang</strong>&nbsp;l&agrave; ranh giới tự nhi&ecirc;n của hai tỉnh Quảng B&igrave;nh v&agrave; H&agrave; Tĩnh một thắng cảnh nổi tiếng m&agrave; mỗi người d&acirc;n Việt ai củng đ&atilde; từng nghe trong b&agrave;i thơ &quot;Qua Đ&egrave;o Ngang&quot; của b&agrave; huyện Thanh Quan.</p>\r\n\r\n			<p>Qu&yacute; kh&aacute;ch dừng ch&acirc;n nghỉ ngơi tr&ecirc;n đỉnh Ho&agrave;nh Sơn Quan, ngắm nh&igrave;n phong cảnh hữu t&igrave;nh nơi đ&acirc;y để hiểu v&igrave; sao Đ&egrave;o Ngang đ&atilde; l&agrave;m rung động bao t&acirc;m hồn của thi nh&acirc;n. Nghe thuyết minh về lịch sử của con đ&egrave;o đ&atilde; từng l&agrave; ranh giới giữa Đại Việt v&agrave; Chi&ecirc;m Th&agrave;nh, l&agrave; một chốt hiểm yếu tr&ecirc;n con đường thi&ecirc;n l&yacute; bắc - &nbsp;nam.</p>\r\n\r\n			<p>Tiếp tục h&agrave;nh tr&igrave;nh qu&yacute; kh&aacute;ch l&ecirc;n đường đến thăm&nbsp;<strong>Ch&ugrave;a Hương T&iacute;ch</strong>&nbsp;được mệnh danh l&agrave; Hoan Ch&acirc;u đệ nhất danh lam, xếp v&agrave;o h&agrave;ng 21 thắng cảnh nước Nam xưa kia.&nbsp;Đ&acirc;y&nbsp;l&agrave; nơi thờ c&ocirc;ng ch&uacute;a Diệu Thi&ecirc;n, con của vua Trang Vương nước Sở.&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>TRƯA</td>\r\n			<td>Xe v&agrave; HDV đưa Qu&yacute; kh&aacute;ch đến nh&agrave; h&agrave;ng nghỉ ngơi v&agrave; d&ugrave;ng bữa trưa.</td>\r\n		</tr>\r\n		<tr>\r\n			<td>CHIỀU</td>\r\n			<td>\r\n			<p>Qu&yacute; kh&aacute;ch đến&nbsp;Miếu C&ocirc; đi c&aacute;p treo, từ tr&ecirc;n cao ngắm n&uacute;i Hồng Lĩnh với những đồi th&ocirc;ng nhấp nh&ocirc; những m&otilde;m đ&aacute; v&agrave; suối nước tạo n&ecirc;n một khung cảnh n&uacute;i non hữu t&igrave;nh.</p>\r\n\r\n			<p><strong>Ch&ugrave;a Hương H&agrave; Tĩnh</strong>&nbsp;ng&ocirc;i ch&ugrave;a cổ k&iacute;nh c&oacute; từ thế kỷ thứ XIII dưới thời nh&agrave; Trần, nghĩa l&agrave; c&oacute; trước ch&ugrave;a Hương H&agrave; Nội h&agrave;ng trăm năm. V&atilde;n cảnh ch&ugrave;a trong kh&ocirc;ng gian cổ k&iacute;nh, linh thi&ecirc;ng th&agrave;nh t&acirc;m hướng về c&otilde;i Phật khấn nguyện cho quốc th&aacute;i, d&acirc;n an, gia đ&igrave;nh hạnh ph&uacute;c. Qu&yacute; kh&aacute;ch sẽ thấy t&acirc;m hồn thanh tịnh, thư th&aacute;i hơn.</p>\r\n\r\n			<p>Tạm biệt Ch&ugrave;a Hương T&iacute;ch, trả kh&aacute;ch về H&agrave; Tĩnh hoặc quay về lại Quảng B&igrave;nh.</p>\r\n\r\n			<p>Thay mặt Phong Nha Explorer chia tay đo&agrave;n, kết th&uacute;c chương tr&igrave;nh, hẹn gặp lại!</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 1, 6, '/storage/tours/1/PD01FHfT6qUuIOZxC8XD.jpg', 'tour-quang-binh-ha-tinh-2-ngay-1dem-7.jpg', '2021-07-23 01:40:04', '2021-07-23 01:40:04', NULL, 'Tour du lịch Quảng Bình Hà Tĩnh 2 ngày 1 đêm khám phá hang động vườn quốc gia Phong Nha Kẻ Bàng và đi lễ chùa Hương Tích cầu phúc với nhiều trải nghiệm thú vị cho kỳ nghỉ của quý khách.', '2021-07-31 15:38:00', 2);

